/**
 * CollaborativeSessionManager - Main orchestrator for multi-LLM collaboration
 * 
 * This class manages the entire lifecycle of collaborative sessions,
 * from initiation through completion, ensuring proper time management,
 * consensus building, and quality assurance.
 */

// vscode not required directly in this manager
import { EventEmitter } from 'events';
import { 
  CollaborativeSession, 
  CollaborationRequest, 
  LLMParticipant, 
  SessionStatus,
  SessionMetrics,
  CollaborationEvent,
  CollaborationError,
  CollaborativeOutput,
  RoundType
} from './types/collaborationTypes';
import { TimeManager } from './timing/TimeManager';
import { CollaborationRound } from './rounds/CollaborationRound';
import { LLMManager } from '../llm/llmManager';
import { VectorDB } from '../db/vectorDB';
import { logger } from '../utils/logger';

export class CollaborativeSessionManager extends EventEmitter {
  private sessions: Map<string, CollaborativeSession> = new Map();
  private timeManager: TimeManager;
  private sessionIdCounter = 0;
  private testMode: boolean = false;

  constructor(
    private llmManager: LLMManager,
    private vectorDB: VectorDB,
    testMode: boolean = false
  ) {
    super();
    this.timeManager = new TimeManager();
    this.testMode = testMode || process.env.NODE_ENV === 'test';
    this.setupEventHandlers();
  }

  /**
   * Start a new collaborative session
   */
  async startSession(request: CollaborationRequest): Promise<CollaborativeSession> {
    const sessionId = `session_${++this.sessionIdCounter}_${Date.now()}`;
    
    logger.info(`üöÄ Starting collaborative session ${sessionId}`);
    logger.debug(`üìù Prompt: ${request.prompt.substring(0, 100)}...`);

    try {
      // Validate request
      this.validateRequest(request);

      // Select participants
      const participants = await this.selectParticipants(request);
      
      // Create session
      const session: CollaborativeSession = {
        id: sessionId,
        initiator: 'user', // TODO: Get actual user context
        participants,
        rounds: [],
        request,
        timeLimit: request.timeLimit || 300000, // 5 minutes default
        consensusThreshold: request.consensusThreshold || 66, // 66% default
        status: 'initializing',
        startTime: new Date(),
        metrics: this.initializeMetrics()
      };

      this.sessions.set(sessionId, session);

      // Set up session timer
      const sessionTimerId = this.timeManager.createSessionTimer(
        sessionId,
        session.timeLimit,
        (remaining) => this.handleSessionWarning(sessionId, remaining),
        () => this.handleSessionTimeout(sessionId)
      );

      // Update status and emit event
      session.status = 'active';
      this.emitEvent('session_started', sessionId, { session });

      // Execute collaboration rounds (skip in test mode to allow manual testing)
      if (!this.testMode) {
        await this.executeRounds(session);
      }

      return session;

    } catch (error) {
      logger.error(`‚ùå Failed to start session ${sessionId}:`, error);
      const collaborationError = error instanceof CollaborationError 
        ? error 
        : new CollaborationError(`Failed to start session: ${error}`, 'SESSION_START_FAILED', sessionId);
      
      this.emitEvent('error', sessionId, { error: collaborationError });
      throw collaborationError;
    }
  }

  /**
   * Execute collaboration rounds for a session
   */
  private async executeRounds(session: CollaborativeSession): Promise<void> {
    const maxRounds = session.request.maxRounds || 4;
    const roundTypes: RoundType[] = ['propose', 'critique', 'synthesize', 'validate'];

    logger.info(`üîÑ Executing up to ${maxRounds} collaboration rounds`);

    for (let roundNumber = 1; roundNumber <= maxRounds; roundNumber++) {
      if (session.status !== 'active') {
        logger.warn(`‚èπÔ∏è Session ${session.id} no longer active, stopping rounds`);
        break;
      }

      const roundType = roundTypes[Math.min(roundNumber - 1, roundTypes.length - 1)];
      
      try {
        logger.info(`üéØ Starting round ${roundNumber}: ${roundType}`);
        
        const round = new CollaborationRound(
          session.id,
          roundNumber,
          roundType,
          this.getRoundPurpose(roundType, roundNumber),
          this.getRoundTimeLimit(roundType)
        );

        // Add round to session
        session.rounds.push(round);
        this.emitEvent('round_started', session.id, { round });

        // Execute the round
        await this.executeRound(session, round);

        // Check if we've reached consensus
        if (this.hasReachedConsensus(session)) {
          logger.info(`‚úÖ Consensus reached in round ${roundNumber}`);
          session.status = 'consensus_reached';
          break;
        }

        // Check quality threshold
        if (this.meetsQualityThreshold(session)) {
          logger.info(`üéØ Quality threshold met in round ${roundNumber}`);
          break;
        }

      } catch (error) {
        logger.error(`‚ùå Error in round ${roundNumber}:`, error);
        // Continue to next round unless critical error
        if (error instanceof CollaborationError && error.code === 'CRITICAL_FAILURE') {
          throw error;
        }
      }
    }

    // Finalize session
    await this.finalizeSession(session);
  }

  /**
   * Execute a single collaboration round
   */
  private async executeRound(session: CollaborativeSession, round: CollaborationRound): Promise<void> {
    // Set up round timer
    const roundTimerId = this.timeManager.createRoundTimer(
      round.type,
      round.timeLimit,
      (remaining) => this.handleRoundWarning(session.id, round.id, remaining),
      () => this.handleRoundTimeout(session.id, round.id)
    );

    round.status = 'active';
    round.startTime = new Date();

    try {
      // Execute round based on type
      switch (round.type) {
        case 'propose':
          await this.executeProposalRound(session, round);
          break;
        case 'critique':
          await this.executeCritiqueRound(session, round);
          break;
        case 'synthesize':
          await this.executeSynthesisRound(session, round);
          break;
        case 'validate':
          await this.executeValidationRound(session, round);
          break;
      }

      round.status = 'completed';
      round.endTime = new Date();

    } catch (error) {
      logger.error(`‚ùå Round ${round.id} failed:`, error);
      round.status = 'timeout';
      throw error;
    } finally {
      this.timeManager.stopTimer(roundTimerId);
    }
  }

  /**
   * Execute a proposal round where LLMs generate initial ideas
   */
  private async executeProposalRound(session: CollaborativeSession, round: CollaborationRound): Promise<void> {
    logger.info(`üí° Executing proposal round`);

    const proposalPrompt = this.buildProposalPrompt(session.request);
    
    // Get proposals from all active participants in parallel
    const proposalPromises = session.participants
      .filter(p => p.isActive)
      .map(async (participant) => {
        try {
          const response = await this.llmManager.generateResponse(
            participant.provider.toLowerCase(),
            proposalPrompt
          );

          const contribution = {
            id: `contrib_${Date.now()}_${participant.id}`,
            roundId: round.id,
            author: participant,
            content: response,
            confidence: 85, // Default confidence for proposals
            buildUpon: [],
            critiques: [],
            timestamp: new Date(),
            tokenCount: this.estimateTokenCount(response),
            metadata: {
              processingTime: 0, // TODO: Track actual processing time
              retryCount: 0
            }
          };

          round.contributions.push(contribution);
          this.emitEvent('contribution_received', session.id, { contribution });
          
          return contribution;

        } catch (error) {
          logger.error(`‚ùå Error getting proposal from ${participant.provider}:`, error);
          return null;
        }
      });

    await Promise.all(proposalPromises);
    
    logger.info(`üí° Collected ${round.contributions.length} proposals`);
  }

  /**
   * Execute a critique round where LLMs review and provide feedback
   */
  private async executeCritiqueRound(session: CollaborativeSession, round: CollaborationRound): Promise<void> {
    logger.info(`üîç Executing critique round`);

    // Get previous round's contributions
    const previousRound = session.rounds[session.rounds.length - 2]; // -1 is current round, -2 is previous
    if (!previousRound || previousRound.contributions.length === 0) {
      throw new CollaborationError('No previous contributions to critique', 'NO_PREVIOUS_CONTRIBUTIONS', session.id, round.id);
    }

    const critiquePrompt = this.buildCritiquePrompt(previousRound.contributions);

    // Get critiques from participants
    const critiquePromises = session.participants
      .filter(p => p.isActive)
      .map(async (participant) => {
        try {
          const response = await this.llmManager.generateResponse(
            participant.provider.toLowerCase(),
            critiquePrompt
          );

          const contribution = {
            id: `contrib_${Date.now()}_${participant.id}`,
            roundId: round.id,
            author: participant,
            content: response,
            confidence: 80,
            buildUpon: previousRound.contributions.map(c => c.id),
            critiques: previousRound.contributions.map(c => c.id),
            timestamp: new Date(),
            tokenCount: this.estimateTokenCount(response),
            metadata: {
              processingTime: 0,
              retryCount: 0
            }
          };

          round.contributions.push(contribution);
          this.emitEvent('contribution_received', session.id, { contribution });
          
          return contribution;

        } catch (error) {
          logger.error(`‚ùå Error getting critique from ${participant.provider}:`, error);
          return null;
        }
      });

    await Promise.all(critiquePromises);
    
    logger.info(`üîç Collected ${round.contributions.length} critiques`);
  }

  /**
   * Execute a synthesis round where ideas are combined
   */
  private async executeSynthesisRound(session: CollaborativeSession, round: CollaborationRound): Promise<void> {
    logger.info(`üîÄ Executing synthesis round`);

    // Get all previous contributions
    const allContributions = session.rounds
      .slice(0, -1) // Exclude current round
      .flatMap(r => r.contributions);

    if (allContributions.length === 0) {
      throw new CollaborationError('No contributions to synthesize', 'NO_CONTRIBUTIONS_TO_SYNTHESIZE', session.id, round.id);
    }

    const synthesisPrompt = this.buildSynthesisPrompt(allContributions);

    // Use the most capable participant for synthesis (TODO: Make this intelligent)
    const synthesizer = this.selectSynthesizer(session.participants);
    
    try {
      const response = await this.llmManager.generateResponse(
        synthesizer.provider.toLowerCase(),
        synthesisPrompt
      );

      const contribution = {
        id: `contrib_${Date.now()}_${synthesizer.id}`,
        roundId: round.id,
        author: synthesizer,
        content: response,
        confidence: 90,
        buildUpon: allContributions.map(c => c.id),
        critiques: [],
        timestamp: new Date(),
        tokenCount: this.estimateTokenCount(response),
        metadata: {
          processingTime: 0,
          retryCount: 0
        }
      };

      round.contributions.push(contribution);
      this.emitEvent('contribution_received', session.id, { contribution });
      
    } catch (error) {
      logger.error(`‚ùå Error in synthesis:`, error);
      throw error;
    }

    logger.info(`üîÄ Synthesis completed`);
  }

  /**
   * Execute a validation round where the synthesized solution is validated
   */
  private async executeValidationRound(session: CollaborativeSession, round: CollaborationRound): Promise<void> {
    logger.info(`‚úÖ Executing validation round`);

    // Get the synthesis from previous round
    const synthesisRound = session.rounds[session.rounds.length - 2];
    if (!synthesisRound || synthesisRound.contributions.length === 0) {
      throw new CollaborationError('No synthesis to validate', 'NO_SYNTHESIS_TO_VALIDATE', session.id, round.id);
    }

    const synthesisContribution = synthesisRound.contributions[0]; // Should be only one synthesis
    const validationPrompt = this.buildValidationPrompt(synthesisContribution, session.request);

    // Get validation from all participants
    const validationPromises = session.participants
      .filter(p => p.isActive && p.id !== synthesisContribution.author.id) // Exclude synthesizer
      .map(async (participant) => {
        try {
          const response = await this.llmManager.generateResponse(
            participant.provider.toLowerCase(),
            validationPrompt
          );

          const contribution = {
            id: `contrib_${Date.now()}_${participant.id}`,
            roundId: round.id,
            author: participant,
            content: response,
            confidence: 85,
            buildUpon: [synthesisContribution.id],
            critiques: [],
            timestamp: new Date(),
            tokenCount: this.estimateTokenCount(response),
            metadata: {
              processingTime: 0,
              retryCount: 0
            }
          };

          round.contributions.push(contribution);
          this.emitEvent('contribution_received', session.id, { contribution });
          
          return contribution;

        } catch (error) {
          logger.error(`‚ùå Error getting validation from ${participant.provider}:`, error);
          return null;
        }
      });

    await Promise.all(validationPromises);
    
    logger.info(`‚úÖ Collected ${round.contributions.length} validations`);
  }

  /**
   * Finalize the collaborative session and generate output
   */
  private async finalizeSession(session: CollaborativeSession): Promise<void> {
    logger.info(`üèÅ Finalizing session ${session.id}`);

    session.endTime = new Date();
    session.status = 'completed';

    // Generate collaborative output
    session.output = await this.generateCollaborativeOutput(session);

    // Update metrics
    this.updateSessionMetrics(session);

    // Store in vector DB for future reference
    await this.storeSessionInVectorDB(session);

    this.emitEvent('session_completed', session.id, { session });

    logger.info(`‚úÖ Session ${session.id} completed successfully`);
    logger.info(`üìä Quality Score: ${session.output.qualityScore}`);
    logger.info(`üéØ Consensus Level: ${session.output.consensusLevel}`);
    logger.info(`‚ö° Token Usage: ${session.output.tokenUsage.totalTokens}`);
  }

  // Helper methods would continue here...
  // Due to length constraints, I'll include the key helper method signatures

  private validateRequest(request: CollaborationRequest): void {
    if (!request.prompt || request.prompt.trim().length === 0) {
      throw new CollaborationError('Request prompt cannot be empty', 'INVALID_REQUEST');
    }
    if (request.priority && !['low', 'medium', 'high', 'critical'].includes(request.priority)) {
      throw new CollaborationError('Invalid priority level', 'INVALID_REQUEST');
    }
    if (request.timeLimit && request.timeLimit < 10000) {
      throw new CollaborationError('Time limit must be at least 10 seconds', 'INVALID_REQUEST');
    }
  }

  private async selectParticipants(request: CollaborationRequest): Promise<LLMParticipant[]> {
    // Create default participants based on available LLM providers
    const participants: LLMParticipant[] = [
      {
        id: 'openai_participant',
        provider: 'OpenAI',
        model: 'gpt-4',
        role: 'implementer',
        strengths: ['implementation', 'code_quality', 'bug_detection'],
        specializations: ['coding', 'technical_analysis'],
        performanceHistory: [],
        isActive: true,
        currentLoad: 0
      },
      {
        id: 'anthropic_participant', 
        provider: 'Anthropic',
        model: 'claude-3-sonnet',
        role: 'reasoner',
        strengths: ['reasoning', 'logic', 'security'],
        specializations: ['architecture', 'analysis'],
        performanceHistory: [],
        isActive: true,
        currentLoad: 0
      },
      {
        id: 'grok_participant',
        provider: 'xAI', 
        model: 'grok-beta',
        role: 'innovator',
        strengths: ['creativity', 'innovation', 'ux_design'],
        specializations: ['creative_solutions', 'user_experience'],
        performanceHistory: [],
        isActive: true,
        currentLoad: 0
      }
    ];

    // Filter by preferred participants if specified
    if (request.preferredParticipants && request.preferredParticipants.length > 0) {
      return participants.filter(p => request.preferredParticipants!.includes(p.provider));
    }

    return participants;
  }

  private initializeMetrics(): SessionMetrics {
    return {
      totalDuration: 0,
      roundCount: 0,
      contributionCount: 0,
      consensusAchieved: false,
      qualityImprovement: 0,
      tokenEfficiency: 0,
      participantUtilization: {},
      emergenceScore: 0
    };
  }

  private getRoundPurpose(type: RoundType, _roundNumber: number): string {
    const purposes = {
      propose: `Generate initial ideas and approaches for the given problem`,
      critique: `Review and provide constructive feedback on previous proposals`,
      synthesize: `Combine the best elements from proposals and critiques into a unified solution`,
      validate: `Validate the synthesized solution for correctness, completeness, and quality`
    };
    return purposes[type];
  }

  private getRoundTimeLimit(type: RoundType): number {
    const limits = {
      propose: 60000,  // 1 minute
      critique: 45000, // 45 seconds
      synthesize: 90000, // 1.5 minutes
      validate: 30000  // 30 seconds
    };
    return limits[type];
  }

  private buildProposalPrompt(request: CollaborationRequest): string {
    return `${request.prompt}\n\nPlease provide your initial proposal for addressing this request. Focus on your unique strengths and perspective.`;
  }

  private buildCritiquePrompt(contributions: any[]): string {
    const contributionTexts = contributions.map(c => `${c.author.provider}: ${c.content}`).join('\n\n');
    return `Please review these proposals and provide constructive critique:\n\n${contributionTexts}\n\nIdentify strengths, weaknesses, and suggestions for improvement.`;
  }

  private buildSynthesisPrompt(contributions: any[]): string {
    const contributionTexts = contributions.map(c => `${c.author.provider}: ${c.content}`).join('\n\n');
    return `Please synthesize these contributions into a unified, comprehensive solution:\n\n${contributionTexts}\n\nCombine the best elements and resolve any conflicts.`;
  }

  private buildValidationPrompt(synthesis: any, request: CollaborationRequest): string {
    return `Please validate this synthesized solution against the original request:\n\nOriginal Request: ${request.prompt}\n\nSynthesized Solution: ${synthesis.content}\n\nProvide validation feedback and any final improvements.`;
  }

  private selectSynthesizer(participants: LLMParticipant[]): LLMParticipant {
    // TODO: Implement intelligent synthesizer selection
    return participants.find(p => p.provider === 'Anthropic') || participants[0];
  }

  private estimateTokenCount(text: string): number {
    // Rough estimation: ~4 characters per token
    return Math.ceil(text.length / 4);
  }

  private hasReachedConsensus(session: CollaborativeSession): boolean {
    // TODO: Implement consensus detection logic
    return false;
  }

  private meetsQualityThreshold(session: CollaborativeSession): boolean {
    // TODO: Implement quality threshold checking
    return false;
  }

  private async generateCollaborativeOutput(session: CollaborativeSession): Promise<CollaborativeOutput> {
    // Get all contributions from all rounds
    const allContributions = session.rounds.flatMap(round => round.contributions);
    
    // Find the final synthesized content (usually from the last synthesis or validation round)
    let finalContent = '';
    const lastRound = session.rounds[session.rounds.length - 1];
    if (lastRound && lastRound.contributions.length > 0) {
      // Use the last contribution as the final content
      finalContent = lastRound.contributions[lastRound.contributions.length - 1].content;
    } else if (allContributions.length > 0) {
      // Fallback: combine all contributions
      finalContent = allContributions.map(c => `**${c.author.provider}**: ${c.content}`).join('\n\n');
    } else {
      finalContent = 'No collaborative output generated - session completed without contributions.';
    }

    // Calculate quality score (average of contribution confidence scores)
    const qualityScore = allContributions.length > 0 
      ? allContributions.reduce((sum, c) => sum + c.confidence, 0) / allContributions.length
      : 0;

    // Determine consensus level
    const consensusLevel = this.determineSessionConsensus(session);

    // Calculate total token usage
    const totalTokens = allContributions.reduce((sum, c) => sum + c.tokenCount, 0);
    const tokensPerParticipant: Record<string, number> = {};
    allContributions.forEach(c => {
      const key = c.author.id;
      tokensPerParticipant[key] = (tokensPerParticipant[key] || 0) + c.tokenCount;
    });

    const tokensPerRound: Record<number, number> = {};
    session.rounds.forEach((round, index) => {
      tokensPerRound[index] = round.contributions.reduce((sum, c) => sum + c.tokenCount, 0);
    });

    return {
      sessionId: session.id,
      content: finalContent,
      sources: allContributions,
      rounds: session.rounds.map(r => r.roundOutput).filter(Boolean) as any[],
      emergenceIndicators: this.calculateEmergenceIndicators(session),
      qualityScore,
      consensusLevel,
      synthesisLog: this.generateSynthesisLog(session),
      improvementMetrics: this.calculateImprovementMetrics(session),
      tokenUsage: {
        totalTokens,
        tokensPerParticipant,
        tokensPerRound,
        efficiency: totalTokens > 0 ? qualityScore / totalTokens * 1000 : 0, // Quality per 1000 tokens
        budgetUtilization: session.request.costBudget ? (totalTokens / session.request.costBudget) * 100 : 0,
        costEstimate: totalTokens * 0.002 // Rough estimate at $0.002 per token
      }
    };
  }

  private updateSessionMetrics(session: CollaborativeSession): void {
    const endTime = session.endTime || new Date();
    session.metrics.totalDuration = endTime.getTime() - session.startTime.getTime();
    session.metrics.roundCount = session.rounds.length;
    session.metrics.contributionCount = session.rounds.reduce((sum, round) => sum + round.contributions.length, 0);
    session.metrics.consensusAchieved = session.status === 'consensus_reached';
    
    if (session.metrics.consensusAchieved && session.metrics.totalDuration > 0) {
      session.metrics.consensusTime = session.metrics.totalDuration;
    }

    // Calculate participant utilization
    const allContributions = session.rounds.flatMap(round => round.contributions);
    session.participants.forEach(participant => {
      const participantContributions = allContributions.filter(c => c.author.id === participant.id);
      session.metrics.participantUtilization[participant.id] = 
        (participantContributions.length / Math.max(session.rounds.length, 1)) * 100;
    });
  }

  private determineSessionConsensus(session: CollaborativeSession): any {
    // Simple implementation - return based on session status
    if (session.status === 'consensus_reached') return 'unanimous';
    if (session.status === 'completed') return 'qualified_majority';
    if (session.status === 'timeout') return 'forced_consensus';
    return 'simple_majority';
  }

  private calculateEmergenceIndicators(_session: CollaborativeSession): any[] {
    // Simple implementation - return empty array for now
    return [];
  }

  private generateSynthesisLog(_session: CollaborativeSession): any[] {
    // Simple implementation - return empty array for now
    return [];
  }

  private calculateImprovementMetrics(_session: CollaborativeSession): any[] {
    // Simple implementation - return empty array for now
    return [];
  }

  private async storeSessionInVectorDB(session: CollaborativeSession): Promise<void> {
    try {
      if (session.output) {
        const sessionSummary = `Collaborative session: ${session.request.prompt}\nResult: ${session.output.content.substring(0, 500)}...`;
        await this.vectorDB.addDocument(
          `session_${session.id}`,
          sessionSummary,
          {
            type: 'collaboration_session',
            sessionId: session.id,
            qualityScore: session.output.qualityScore,
            consensusLevel: session.output.consensusLevel,
            participants: session.participants.map(p => p.provider),
            timestamp: session.startTime.toISOString()
          }
        );
      }
    } catch (error) {
      logger.warn(`Failed to store session ${session.id} in VectorDB:`, error);
      // Don't throw - this is not critical for session completion
    }
  }

  private handleSessionWarning(sessionId: string, remaining: number): void {
    logger.warn(`‚ö†Ô∏è Session ${sessionId} time warning: ${Math.round(remaining/1000)}s remaining`);
    this.emitEvent('timeout_warning', sessionId, { remaining });
  }

  private handleSessionTimeout(sessionId: string): void {
    logger.warn(`‚è∞ Session ${sessionId} timeout - forcing completion`);
    const session = this.sessions.get(sessionId);
    if (session) {
      session.status = 'timeout';
      this.finalizeSession(session);
    }
  }

  private handleRoundWarning(sessionId: string, roundId: string, remaining: number): void {
    logger.warn(`‚ö†Ô∏è Round ${roundId} time warning: ${Math.round(remaining/1000)}s remaining`);
  }

  private handleRoundTimeout(sessionId: string, roundId: string): void {
    logger.warn(`‚è∞ Round ${roundId} timeout`);
  }

  private setupEventHandlers(): void {
    // Set up internal event handling
  }

  private emitEvent(type: string, sessionId: string, data: any): void {
    const event: CollaborationEvent = {
      type: type as any,
      sessionId,
      timestamp: new Date(),
      data
    };
    this.emit(type, event);
  }

  /**
   * Get session by ID
   */
  getSession(sessionId: string): CollaborativeSession | undefined {
    return this.sessions.get(sessionId);
  }

  /**
   * Get all active sessions
   */
  getActiveSessions(): CollaborativeSession[] {
    return Array.from(this.sessions.values()).filter(s => s.status === 'active');
  }

  /**
   * Complete a session and return final results
   */
  async completeSession(sessionId: string): Promise<CollaborativeOutput> {
    const session = this.sessions.get(sessionId);
    if (!session) {
      throw new CollaborationError(`Session ${sessionId} not found`, 'SESSION_NOT_FOUND');
    }

    // Generate final synthesis and metrics
    const output = await this.generateCollaborativeOutput(session);
    
    // Update session status
    session.status = 'completed';
    session.endTime = new Date();
    
    // Store results in vector DB
    session.output = output; // Set the output first
    await this.storeSessionInVectorDB(session);
    
    logger.info(`‚úÖ Completed collaborative session ${sessionId}`);
    this.emit('session_completed', { sessionId, output });
    
    return output;
  }

  /**
   * Manually execute rounds for a session (for testing)
   */
  async executeSessionRounds(sessionId: string): Promise<void> {
    const session = this.sessions.get(sessionId);
    if (!session) {
      throw new Error(`Session ${sessionId} not found`);
    }
    await this.executeRounds(session);
  }

  /**
   * Dispose of the session manager
   */
  dispose(): void {
    this.timeManager.dispose();
    this.sessions.clear();
    this.removeAllListeners();
  }
}

/**
 * CollaborationRound - Manages individual rounds within a collaborative session
 * 
 * Each round has a specific purpose (propose, critique, synthesize, validate)
 * and manages the collection and processing of contributions from LLM participants.
 */

import { 
  CollaborationRound as ICollaborationRound,
  RoundType,
  Contribution,
  RoundOutput,
  LLMParticipant,
  ConsensusLevel
} from '../types/collaborationTypes';
import { logger } from '../../utils/logger';

export class CollaborationRound implements ICollaborationRound {
  public id: string;
  public sessionId: string;
  public roundNumber: number;
  public type: RoundType;
  public purpose: string;
  public timeLimit: number;
  public startTime: Date;
  public endTime?: Date;
  public contributions: Contribution[] = [];
  public roundOutput?: RoundOutput;
  public status: 'pending' | 'active' | 'completed' | 'timeout' = 'pending';

  constructor(
    sessionId: string,
    roundNumber: number,
    type: RoundType,
    purpose: string,
    timeLimit: number
  ) {
    this.id = `round_${sessionId}_${roundNumber}_${type}`;
    this.sessionId = sessionId;
    this.roundNumber = roundNumber;
    this.type = type;
    this.purpose = purpose;
    this.timeLimit = timeLimit;
    this.startTime = new Date();
  }

  /**
   * Add a contribution to this round
   */
  addContribution(contribution: Contribution): void {
    if (this.status !== 'active') {
      throw new Error(`Cannot add contribution to round ${this.id} with status ${this.status}`);
    }

    contribution.roundId = this.id;
    this.contributions.push(contribution);
    
    logger.debug(`üìù Added contribution from ${contribution.author.provider} to round ${this.type}`);
  }

  /**
   * Get contributions by participant
   */
  getContributionsByParticipant(participantId: string): Contribution[] {
    return this.contributions.filter(c => c.author.id === participantId);
  }

  /**
   * Get the average confidence score for this round
   */
  getAverageConfidence(): number {
    if (this.contributions.length === 0) {
      return 0;
    }

    const totalConfidence = this.contributions.reduce((sum, c) => sum + c.confidence, 0);
    return totalConfidence / this.contributions.length;
  }

  /**
   * Get total token count for this round
   */
  getTotalTokenCount(): number {
    return this.contributions.reduce((sum, c) => sum + c.tokenCount, 0);
  }

  /**
   * Check if all expected participants have contributed
   */
  hasAllContributions(expectedParticipants: LLMParticipant[]): boolean {
    const contributorIds = new Set(this.contributions.map(c => c.author.id));
    return expectedParticipants.every(p => contributorIds.has(p.id));
  }

  /**
   * Generate round output summary
   */
  generateRoundOutput(): RoundOutput {
    const consensusLevel = this.determineConsensusLevel();
    const qualityScore = this.calculateQualityScore();
    
    this.roundOutput = {
      roundId: this.id,
      type: this.type,
      synthesizedContent: this.synthesizeContributions(),
      participatingContributions: this.contributions.map(c => c.id),
      consensusLevel,
      qualityScore,
      emergenceIndicators: [], // TODO: Implement emergence detection
      nextRoundRecommendation: this.recommendNextRound()
    };

    return this.roundOutput;
  }

  /**
   * Determine consensus level based on contribution analysis
   */
  private determineConsensusLevel(): ConsensusLevel {
    if (this.contributions.length === 0) {
      return 'forced_consensus';
    }

    // Simple implementation - can be enhanced with semantic analysis
    const agreements = this.analyzeAgreements();
    
    if (agreements >= 0.9) {
      return 'unanimous';
    } else if (agreements >= 0.66) {
      return 'qualified_majority';
    } else if (agreements >= 0.51) {
      return 'simple_majority';
    } else {
      return 'forced_consensus';
    }
  }

  /**
   * Analyze agreement levels between contributions
   */
  private analyzeAgreements(): number {
    if (this.contributions.length <= 1) {
      return 1.0; // Single contribution is 100% agreement with itself
    }

    // Simple implementation: average confidence as proxy for agreement
    // TODO: Implement semantic similarity analysis
    const avgConfidence = this.getAverageConfidence();
    return avgConfidence / 100;
  }

  /**
   * Calculate quality score for this round
   */
  private calculateQualityScore(): number {
    if (this.contributions.length === 0) {
      return 0;
    }

    // Factors: average confidence, contribution count, diversity
    const avgConfidence = this.getAverageConfidence();
    const contributionBonus = Math.min(this.contributions.length * 10, 30); // Up to 30 points for participation
    const diversityBonus = this.calculateDiversityBonus();

    return Math.min(100, avgConfidence + contributionBonus + diversityBonus);
  }

  /**
   * Calculate diversity bonus based on different providers contributing
   */
  private calculateDiversityBonus(): number {
    const uniqueProviders = new Set(this.contributions.map(c => c.author.provider));
    return uniqueProviders.size * 5; // 5 points per unique provider
  }

  /**
   * Synthesize contributions into a summary
   */
  private synthesizeContributions(): string {
    if (this.contributions.length === 0) {
      return `No contributions received for ${this.type} round.`;
    }

    if (this.contributions.length === 1) {
      return this.contributions[0].content;
    }

    // Simple synthesis - can be enhanced with LLM-powered synthesis
    const contributionSummaries = this.contributions.map((c, _index) => 
      `**${c.author.provider}** (Confidence: ${c.confidence}%): ${c.content.substring(0, 200)}...`
    );

    return `**${this.type.toUpperCase()} ROUND SYNTHESIS**\n\n${contributionSummaries.join('\n\n')}`;
  }

  /**
   * Recommend the next round type based on current round results
   */
  private recommendNextRound(): RoundType | undefined {
    const roundSequence: RoundType[] = ['propose', 'critique', 'synthesize', 'validate'];
    const currentIndex = roundSequence.indexOf(this.type);
    
    if (currentIndex < roundSequence.length - 1) {
      return roundSequence[currentIndex + 1];
    }

    // If we're at the last round, check if we need more iterations
    const qualityScore = this.calculateQualityScore();
    if (qualityScore < 70) {
      return 'propose'; // Start over if quality is too low
    }

    return undefined; // No more rounds needed
  }

  /**
   * Get round duration in milliseconds
   */
  getDuration(): number {
    if (!this.endTime) {
      return Date.now() - this.startTime.getTime();
    }
    return this.endTime.getTime() - this.startTime.getTime();
  }

  /**
   * Check if round is within time limit
   */
  isWithinTimeLimit(): boolean {
    return this.getDuration() <= this.timeLimit;
  }

  /**
   * Get round statistics
   */
  getStatistics(): {
    duration: number;
    contributionCount: number;
    averageConfidence: number;
    totalTokens: number;
    uniqueProviders: number;
    qualityScore: number;
  } {
    return {
      duration: this.getDuration(),
      contributionCount: this.contributions.length,
      averageConfidence: this.getAverageConfidence(),
      totalTokens: this.getTotalTokenCount(),
      uniqueProviders: new Set(this.contributions.map(c => c.author.provider)).size,
      qualityScore: this.calculateQualityScore()
    };
  }

  /**
   * Complete the round and generate final output
   */
  complete(): RoundOutput {
    this.status = 'completed';
    this.endTime = new Date();
    
    const output = this.generateRoundOutput();
    
    logger.info(`‚úÖ Round ${this.type} completed:`);
    logger.debug(`   Duration: ${Math.round(this.getDuration()/1000)}s`);
    logger.debug(`   Contributions: ${this.contributions.length}`);
    logger.debug(`   Quality Score: ${output.qualityScore}`);
    logger.debug(`   Consensus: ${output.consensusLevel}`);
    
    return output;
  }

  /**
   * Mark round as timed out
   */
  timeout(): RoundOutput {
    this.status = 'timeout';
    this.endTime = new Date();
    
    const output = this.generateRoundOutput();
    
    logger.warn(`‚è∞ Round ${this.type} timed out:`);
    logger.debug(`   Partial contributions: ${this.contributions.length}`);
    logger.debug(`   Quality Score: ${output.qualityScore}`);
    
    return output;
  }
}

/**
 * TimeManager - Handles time limits, timeouts, and warnings for collaborative sessions
 * 
 * This class provides robust timing functionality with countdown warnings,
 * graceful timeout handling, and configurable time limits for different
 * collaboration scenarios.
 */

import { TimeManager as ITimeManager, TimerCallbacks, CollaborationError as _CollaborationError } from '../types/collaborationTypes';
import { logger } from '../../utils/logger';

interface ActiveTimer {
  id: string;
  startTime: Date;
  duration: number; // milliseconds
  callbacks?: TimerCallbacks;
  warningsSent: boolean[];
  isActive: boolean;
  timeoutHandle?: NodeJS.Timeout;
  warningHandles: NodeJS.Timeout[];
}

export class TimeManager implements ITimeManager {
  private timers: Map<string, ActiveTimer> = new Map();
  private timerIdCounter = 0;

  // Warning thresholds as percentages of total time
  private readonly WARNING_THRESHOLDS = [0.75, 0.90]; // 75% and 90%

  /**
   * Start a new timer with specified duration and callbacks
   */
  startTimer(duration: number, callbacks?: TimerCallbacks): string {
    const timerId = `timer_${++this.timerIdCounter}_${Date.now()}`;
    const startTime = new Date();

    const timer: ActiveTimer = {
      id: timerId,
      startTime,
      duration,
      callbacks,
      warningsSent: new Array(this.WARNING_THRESHOLDS.length).fill(false),
      isActive: true,
      warningHandles: []
    };

    // Set up warning timers
    this.setupWarningTimers(timer);

    // Set up timeout timer
    timer.timeoutHandle = setTimeout(() => {
      this.handleTimeout(timerId);
    }, duration);

    this.timers.set(timerId, timer);

    logger.debug(`‚è∞ Timer ${timerId} started for ${duration}ms`);
    return timerId;
  }

  /**
   * Stop an active timer
   */
  stopTimer(timerId: string): void {
    const timer = this.timers.get(timerId);
    if (!timer) {
      logger.warn(`Timer ${timerId} not found`);
      return;
    }

    timer.isActive = false;

    // Clear timeout
    if (timer.timeoutHandle) {
      clearTimeout(timer.timeoutHandle);
    }

    // Clear warning timers
    timer.warningHandles.forEach(handle => clearTimeout(handle));

    this.timers.delete(timerId);
    logger.debug(`‚è∞ Timer ${timerId} stopped`);
  }

  /**
   * Get remaining time for a timer in milliseconds
   */
  getRemainingTime(timerId: string): number {
    const timer = this.timers.get(timerId);
    if (!timer || !timer.isActive) {
      return 0;
    }

    const elapsed = Date.now() - timer.startTime.getTime();
    const remaining = Math.max(0, timer.duration - elapsed);
    return remaining;
  }

  /**
   * Check if a timer has expired
   */
  isExpired(timerId: string): boolean {
    const timer = this.timers.get(timerId);
    if (!timer) {
      return true; // Non-existent timers are considered expired
    }

    return !timer.isActive || this.getRemainingTime(timerId) <= 0;
  }

  /**
   * Get timer progress as percentage (0-100)
   */
  getProgress(timerId: string): number {
    const timer = this.timers.get(timerId);
    if (!timer || !timer.isActive) {
      return 100; // Completed or non-existent
    }

    const elapsed = Date.now() - timer.startTime.getTime();
    const progress = Math.min(100, (elapsed / timer.duration) * 100);
    return progress;
  }

  /**
   * Extend a timer's duration
   */
  extendTimer(timerId: string, additionalTime: number): boolean {
    const timer = this.timers.get(timerId);
    if (!timer || !timer.isActive) {
      return false;
    }

    // Clear existing timeout
    if (timer.timeoutHandle) {
      clearTimeout(timer.timeoutHandle);
    }

    // Clear existing warning timers
    timer.warningHandles.forEach(handle => clearTimeout(handle));
    timer.warningHandles = [];

    // Update duration
    timer.duration += additionalTime;

    // Reset warnings
    timer.warningsSent.fill(false);

    // Set up new timers
    this.setupWarningTimers(timer);
    timer.timeoutHandle = setTimeout(() => {
      this.handleTimeout(timerId);
    }, this.getRemainingTime(timerId));

    logger.debug(`‚è∞ Timer ${timerId} extended by ${additionalTime}ms`);
    return true;
  }

  /**
   * Get statistics for all active timers
   */
  getActiveTimerStats(): { count: number; timers: { id: string; remaining: number; progress: number }[] } {
    const activeTimers = Array.from(this.timers.values()).filter(t => t.isActive);
    
    return {
      count: activeTimers.length,
      timers: activeTimers.map(timer => ({
        id: timer.id,
        remaining: this.getRemainingTime(timer.id),
        progress: this.getProgress(timer.id)
      }))
    };
  }

  /**
   * Clean up expired timers
   */
  cleanup(): void {
    const expiredTimers = Array.from(this.timers.entries())
      .filter(([_, timer]) => !timer.isActive)
      .map(([id, _]) => id);

    expiredTimers.forEach(timerId => {
      this.timers.delete(timerId);
    });

    logger.debug(`‚è∞ Cleaned up ${expiredTimers.length} expired timers`);
  }

  /**
   * Set up warning timers based on thresholds
   */
  private setupWarningTimers(timer: ActiveTimer): void {
    if (!timer.callbacks?.onWarning) {
      return; // No warning callback provided
    }

    this.WARNING_THRESHOLDS.forEach((threshold, index) => {
      const warningTime = timer.duration * threshold;
      
      const warningHandle = setTimeout(() => {
        if (timer.isActive && !timer.warningsSent[index]) {
          timer.warningsSent[index] = true;
          const remainingTime = this.getRemainingTime(timer.id);
          
          logger.warn(`‚ö†Ô∏è Timer ${timer.id} warning: ${remainingTime}ms remaining (${Math.round((1-threshold) * 100)}% left)`);
          
          try {
            timer.callbacks!.onWarning!(remainingTime);
          } catch (error) {
            logger.error(`Error in warning callback for timer ${timer.id}:`, error);
          }
        }
      }, warningTime);

      timer.warningHandles.push(warningHandle);
    });
  }

  /**
   * Handle timer timeout
   */
  private handleTimeout(timerId: string): void {
    const timer = this.timers.get(timerId);
    if (!timer || !timer.isActive) {
      return;
    }

    timer.isActive = false;

    logger.warn(`‚è∞ Timer ${timerId} expired`);

    // Call timeout callback if provided
    if (timer.callbacks?.onTimeout) {
      try {
        timer.callbacks.onTimeout();
      } catch (error) {
        logger.error(`Error in timeout callback for timer ${timerId}:`, error);
      }
    }

    // Clean up warning timers
    timer.warningHandles.forEach(handle => clearTimeout(handle));
    timer.warningHandles = [];
  }

  /**
   * Create a timer for a collaboration round with standard settings
   */
  createRoundTimer(
    roundType: string,
    duration: number,
    onWarning?: (remaining: number) => void,
    onTimeout?: () => void
  ): string {
    const callbacks: TimerCallbacks = {};
    
    if (onWarning) {
      callbacks.onWarning = (remaining) => {
        logger.warn(`‚ö†Ô∏è Round ${roundType} time warning: ${Math.round(remaining/1000)}s remaining`);
        onWarning(remaining);
      };
    }

    if (onTimeout) {
      callbacks.onTimeout = () => {
        logger.warn(`‚è∞ Round ${roundType} timeout reached`);
        onTimeout();
      };
    }

    return this.startTimer(duration, callbacks);
  }

  /**
   * Create a timer for an entire collaboration session
   */
  createSessionTimer(
    sessionId: string,
    duration: number,
    onWarning?: (remaining: number) => void,
    onTimeout?: () => void
  ): string {
    const callbacks: TimerCallbacks = {};
    
    if (onWarning) {
      callbacks.onWarning = (remaining) => {
        logger.warn(`‚ö†Ô∏è Session ${sessionId} time warning: ${Math.round(remaining/1000)}s remaining`);
        onWarning(remaining);
      };
    }

    if (onTimeout) {
      callbacks.onTimeout = () => {
        logger.warn(`‚è∞ Session ${sessionId} timeout - forcing consensus`);
        onTimeout();
      };
    }

    return this.startTimer(duration, callbacks);
  }

  /**
   * Dispose of all timers and clean up resources
   */
  dispose(): void {
    logger.debug('‚è∞ TimeManager disposing all timers...');
    
    for (const [timerId] of this.timers) {
      this.stopTimer(timerId);
    }
    
    this.timers.clear();
    this.timerIdCounter = 0;
  }
}

/**
 * Singleton instance for global use
 */
export const timeManager = new TimeManager();

/**
 * Core type definitions for the True Multi-LLM Collaboration System
 * 
 * This file defines all interfaces, types, and enums used throughout
 * the collaboration system to ensure type safety and consistency.
 */

export type LLMProvider = 'OpenAI' | 'Anthropic' | 'xAI' | 'OpenRouter';
export type SessionStatus = 'initializing' | 'active' | 'consensus_reached' | 'timeout' | 'completed' | 'failed';
export type RoundType = 'propose' | 'critique' | 'synthesize' | 'validate';
export type ConsensusLevel = 'unanimous' | 'qualified_majority' | 'simple_majority' | 'forced_consensus';

/**
 * Core collaboration session interface
 */
export interface CollaborativeSession {
  id: string;
  initiator: string;
  participants: LLMParticipant[];
  rounds: CollaborationRound[];
  request: CollaborationRequest;
  timeLimit: number; // in milliseconds
  consensusThreshold: number; // percentage (0-100)
  status: SessionStatus;
  startTime: Date;
  endTime?: Date;
  metrics: SessionMetrics;
  output?: CollaborativeOutput;
}

/**
 * LLM participant in a collaborative session
 */
export interface LLMParticipant {
  id: string;
  provider: LLMProvider;
  model: string;
  role: string;
  strengths: string[];
  specializations: string[];
  performanceHistory: PerformanceRecord[];
  isActive: boolean;
  currentLoad: number; // 0-100 percentage
}

/**
 * Individual collaboration round
 */
export interface CollaborationRound {
  id: string;
  sessionId: string;
  roundNumber: number;
  type: RoundType;
  purpose: string;
  timeLimit: number;
  startTime: Date;
  endTime?: Date;
  contributions: Contribution[];
  roundOutput?: RoundOutput;
  status: 'pending' | 'active' | 'completed' | 'timeout';
}

/**
 * Individual contribution from an LLM participant
 */
export interface Contribution {
  id: string;
  roundId: string;
  author: LLMParticipant;
  content: string;
  confidence: number; // 0-100
  buildUpon: string[]; // IDs of contributions this builds upon
  critiques: string[]; // IDs of contributions this critiques
  timestamp: Date;
  tokenCount: number;
  metadata: ContributionMetadata;
}

/**
 * Metadata for contributions
 */
export interface ContributionMetadata {
  processingTime: number; // milliseconds
  retryCount: number;
  qualityScore?: number;
  semanticSimilarity?: number;
  innovationScore?: number;
  technicalAccuracy?: number;
}

/**
 * Output from a collaboration round
 */
export interface RoundOutput {
  roundId: string;
  type: RoundType;
  synthesizedContent: string;
  participatingContributions: string[]; // contribution IDs
  consensusLevel: ConsensusLevel;
  qualityScore: number;
  emergenceIndicators: EmergenceMetric[];
  nextRoundRecommendation?: RoundType;
}

/**
 * Final output from a collaborative session
 */
export interface CollaborativeOutput {
  sessionId: string;
  content: string;
  sources: Contribution[];
  rounds: RoundOutput[];
  emergenceIndicators: EmergenceMetric[];
  qualityScore: number;
  consensusLevel: ConsensusLevel;
  synthesisLog: SynthesisStep[];
  improvementMetrics: ImprovementMetric[];
  tokenUsage: TokenUsageMetrics;
}

/**
 * Request to initiate a collaborative session
 */
export interface CollaborationRequest {
  prompt: string;
  context?: string;
  requirements?: string[];
  constraints?: string[];
  preferredParticipants?: LLMProvider[];
  maxRounds?: number;
  timeLimit?: number;
  consensusThreshold?: number;
  qualityThreshold?: number;
  costBudget?: number; // in tokens
  priority: 'low' | 'medium' | 'high' | 'critical';
}

/**
 * Metrics for tracking session performance
 */
export interface SessionMetrics {
  totalDuration: number;
  roundCount: number;
  contributionCount: number;
  consensusAchieved: boolean;
  consensusTime?: number;
  qualityImprovement: number; // percentage vs single-LLM baseline
  tokenEfficiency: number; // quality per token
  participantUtilization: Record<string, number>; // participant ID -> utilization %
  emergenceScore: number; // measure of emergent intelligence
}

/**
 * Performance record for LLM participants
 */
export interface PerformanceRecord {
  sessionId: string;
  taskType: string;
  qualityScore: number;
  contributionValue: number;
  collaborationEffectiveness: number;
  tokenEfficiency: number;
  timestamp: Date;
}

/**
 * Metrics indicating emergent intelligence
 */
export interface EmergenceMetric {
  type: 'novel_solution' | 'synthesis_quality' | 'collective_insight' | 'innovation_leap';
  score: number; // 0-100
  description: string;
  evidence: string[];
  contributingSources: string[]; // contribution IDs
}

/**
 * Step in the synthesis process
 */
export interface SynthesisStep {
  stepNumber: number;
  operation: 'merge' | 'resolve_conflict' | 'enhance' | 'validate';
  inputs: string[]; // contribution IDs
  output: string;
  reasoning: string;
  confidenceScore: number;
}

/**
 * Metrics showing improvement over single-LLM baseline
 */
export interface ImprovementMetric {
  dimension: 'quality' | 'innovation' | 'completeness' | 'accuracy' | 'creativity';
  baselineScore: number;
  collaborativeScore: number;
  improvement: number; // percentage
  significance: 'minor' | 'moderate' | 'major' | 'breakthrough';
}

/**
 * Token usage tracking
 */
export interface TokenUsageMetrics {
  totalTokens: number;
  tokensPerParticipant: Record<string, number>;
  tokensPerRound: Record<number, number>;
