}

/**
 * Configuration for collaboration system
 */
export interface CollaborationConfig {
  defaultTimeLimit: number;
  defaultConsensusThreshold: number;
  maxParticipants: number;
  maxRounds: number;
  qualityThreshold: number;
  tokenBudgetDefault: number;
  enableCaching: boolean;
  enableLearning: boolean;
  debugMode: boolean;
}

/**
 * Events emitted by the collaboration system
 */
export interface CollaborationEvent {
  type: 'session_started' | 'round_started' | 'contribution_received' | 
        'consensus_reached' | 'timeout_warning' | 'session_completed' | 'error';
  sessionId: string;
  timestamp: Date;
  data: any;
}

/**
 * Error types specific to collaboration system
 */
export class CollaborationError extends Error {
  constructor(
    message: string,
    public code: string,
    public sessionId?: string,
    public roundId?: string
  ) {
    super(message);
    this.name = 'CollaborationError';
  }
}

/**
 * Time management related interfaces
 */
export interface TimeManager {
  startTimer(duration: number, callbacks?: TimerCallbacks): string;
  stopTimer(timerId: string): void;
  getRemainingTime(timerId: string): number;
  isExpired(timerId: string): boolean;
}

export interface TimerCallbacks {
  onWarning?: (remainingTime: number) => void;
  onTimeout?: () => void;
  onTick?: (remainingTime: number) => void;
}

/**
 * Consensus building interfaces
 */
export interface ConsensusResult {
  achieved: boolean;
  level: ConsensusLevel;
  agreement: number; // percentage
  dissent: string[]; // minority opinions
  finalDecision: string;
  votingRecord?: VotingRecord;
}

export interface VotingRecord {
  options: string[];
  votes: Record<string, string>; // participant ID -> voted option
  results: Record<string, number>; // option -> vote count
  winner: string;
  margin: number; // percentage
}

/**
 * Task assignment interfaces
 */
export interface TaskAssignment {
  taskId: string;
  assignedParticipant: string; // participant ID
  rationale: string;
  specialization: string;
  priority: number;
  dependencies: string[]; // other task IDs
  estimatedTokens: number;
  estimatedDuration: number; // milliseconds
}

export interface StrengthProfile {
  participantId: string;
  strengths: Record<string, number>; // strength category -> score (0-100)
  weaknesses: string[];
  specializations: string[];
  historicalAccuracy: number;
  adaptabilityScore: number;
  collaborationScore: number;
  lastUpdated: Date;
}

import * as path from 'path';
import * as fs from 'fs';
import { HfInference } from '@huggingface/inference';

interface VectorItem {
  id: string;
  vector: number[];
  metadata: any;
}

export class VectorDB {
  private items: VectorItem[] = [];
  private storagePath: string;
  private hf: HfInference;
  private embeddingModel = 'sentence-transformers/all-MiniLM-L6-v2';

  constructor(storagePath: string) {
    this.storagePath = path.join(storagePath, 'vectordb');
    // Initialize Hugging Face inference - will use public API or user's token if set
    this.hf = new HfInference(process.env.HUGGINGFACE_API_TOKEN);
  }

  async init() {
    // Ensure storage directory exists
    if (!fs.existsSync(this.storagePath)) {
      fs.mkdirSync(this.storagePath, { recursive: true });
    }

    // Load existing data if available
    const dataPath = path.join(this.storagePath, 'vectors.json');
    if (fs.existsSync(dataPath)) {
      try {
        const data = fs.readFileSync(dataPath, 'utf8');
        this.items = JSON.parse(data);
      } catch (error) {
        console.warn('Failed to load vector database:', error);
        this.items = [];
      }
    }
  }

  async addEmbedding(key: string, vector: number[], metadata: any) {
    const existingIndex = this.items.findIndex(item => item.id === key);
    if (existingIndex >= 0) {
      this.items[existingIndex] = { id: key, vector, metadata };
    } else {
      this.items.push({ id: key, vector, metadata });
    }
    await this.save();
  }

  async queryEmbedding(
    vector: number[],
    topK: number = 5
  ): Promise<(VectorItem & { similarity: number })[]> {
    // Simple cosine similarity implementation
    const similarities = this.items.map(item => ({
      item,
      similarity: this.cosineSimilarity(vector, item.vector),
    }));

    similarities.sort((a, b) => b.similarity - a.similarity);
    return similarities.slice(0, topK).map(s => ({ ...s.item, similarity: s.similarity }));
  }

  private cosineSimilarity(a: number[], b: number[]): number {
    if (a.length !== b.length) return 0;

    let dotProduct = 0;
    let normA = 0;
    let normB = 0;

    for (let i = 0; i < a.length; i++) {
      dotProduct += a[i] * b[i];
      normA += a[i] * a[i];
      normB += b[i] * b[i];
    }

    return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
  }

  async save() {
    const dataPath = path.join(this.storagePath, 'vectors.json');
    fs.writeFileSync(dataPath, JSON.stringify(this.items, null, 2));
  }

  close() {
    // Cleanup if needed
  }

  // Real embedding implementation using Hugging Face
  async getEmbedding(text: string): Promise<number[]> {
    try {
      // Clean and prepare text for embedding
      const cleanText = text.replace(/\s+/g, ' ').trim();

      if (!cleanText) {
        console.warn('Empty text provided for embedding, using fallback');
        return this.getFallbackEmbedding(text);
      }

      // Use Hugging Face inference for real embeddings
      const response = await this.hf.featureExtraction({
        model: this.embeddingModel,
        inputs: cleanText,
      });

      // Handle different response formats
      let embedding: number[];
      if (Array.isArray(response)) {
        if (Array.isArray(response[0])) {
          embedding = response[0] as number[];
        } else {
          embedding = response as number[];
        }
      } else {
        throw new Error('Unexpected response format from embedding API');
      }

      // Validate embedding
      if (!embedding || embedding.length === 0) {
        console.warn('Invalid embedding received, using fallback');
        return this.getFallbackEmbedding(text);
      }

      console.log(
        `Generated embedding for "${cleanText.substring(0, 50)}..." (${embedding.length} dimensions)`
      );
      return embedding;
    } catch (error: any) {
      console.warn(`Embedding API failed: ${error.message}, using fallback`);
      return this.getFallbackEmbedding(text);
    }
  }

  // Fallback embedding for when API is unavailable
  private getFallbackEmbedding(text: string): number[] {
    console.log('Using fallback embedding generation');
    const hash = this.simpleHash(text);
    const embedding = new Array(384).fill(0); // Standard embedding dimension

    // Create a deterministic but distributed embedding based on text
    for (let i = 0; i < embedding.length; i++) {
      embedding[i] = Math.sin((hash + i) * 0.1) * Math.cos((hash + i) * 0.2);
    }

    return embedding;
  }

  private simpleHash(str: string): number {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return Math.abs(hash);
  }

  // Batch embedding for efficiency
  async getBatchEmbeddings(texts: string[]): Promise<number[][]> {
    const embeddings: number[][] = [];

    // Process in batches to avoid rate limits
    const batchSize = 5;
    for (let i = 0; i < texts.length; i += batchSize) {
      const batch = texts.slice(i, i + batchSize);
      const batchPromises = batch.map(text => this.getEmbedding(text));

      try {
        const batchResults = await Promise.all(batchPromises);
        embeddings.push(...batchResults);

        // Small delay to respect rate limits
        if (i + batchSize < texts.length) {
          await new Promise(resolve => setTimeout(resolve, 100));
        }
      } catch (error: any) {
        console.warn(`Batch embedding failed: ${error.message}`);
        // Add fallback embeddings for failed batch
        for (const text of batch) {
          embeddings.push(this.getFallbackEmbedding(text));
        }
      }
    }

    return embeddings;
  }
}

/**
 * AstraForge IDE Extension Entry Point
 * Optimized for fast activation with lazy loading
 */

import * as vscode from 'vscode';

// Lazy-loaded module references
let llmManager: any;
let vectorDB: any;
let workflowManager: any;
let gitManager: any;

export async function activate(context: vscode.ExtensionContext) {
  console.log('AstraForge IDE activated! Launching into the stratosphere...');

  // Register providers immediately but lazy-load heavy modules
  await registerProviders(context);

  // Register commands
  registerCommands(context);

  // Initialize heavy modules only when needed
  await initializeManagers(context);

  console.log('AstraForge IDE fully activated');
}

/**
 * Register webview providers immediately for UI responsiveness
 */
async function registerProviders(context: vscode.ExtensionContext) {
  // Setup Wizard - lightweight, load immediately
  const { SetupWizardProvider } = await import('./providers/setupWizard');
  const setupWizard = new SetupWizardProvider(context.extensionUri);
  context.subscriptions.push(
    vscode.window.registerWebviewViewProvider('astraforge.setupWizard', setupWizard)
  );

  // API Tester - load immediately for testing
  const { ApiTesterProvider } = await import('./testing/apiTesterProvider');
  const apiTester = new ApiTesterProvider(context.extensionUri);
  context.subscriptions.push(
    vscode.window.registerWebviewViewProvider('astraforge.apiTester', apiTester)
  );

  // Project Ignition - delay until workflow is needed
  let projectIgnition: any;
  const getProjectIgnition = async () => {
    if (!projectIgnition) {
      const { ProjectIgnitionProvider } = await import('./providers/projectIgnition');
      await ensureWorkflowManager(context);
      projectIgnition = new ProjectIgnitionProvider(context.extensionUri, workflowManager);
    }
    return projectIgnition;
  };

  context.subscriptions.push(
    vscode.window.registerWebviewViewProvider('astraforge.projectIgnition', {
      resolveWebviewView: async (webviewView, context, token) => {
        const provider = await getProjectIgnition();
        return provider.resolveWebviewView(webviewView, context, token);
      },
    })
  );
}

/**
 * Register extension commands
 */
function registerCommands(context: vscode.ExtensionContext) {
  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.setupPanel', async () => {
      await vscode.commands.executeCommand('workbench.action.focusSideBar');
      await vscode.commands.executeCommand('workbench.view.extension.astraforge-activitybar');
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.submitIdea', async (idea: string) => {
      await ensureWorkflowManager(context);
      workflowManager.startWorkflow(idea);
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.proceedPhase', async () => {
      await ensureWorkflowManager(context);
      workflowManager.proceedToNextPhase();
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.testAPIs', async () => {
      await vscode.commands.executeCommand('workbench.action.focusSideBar');
      await vscode.commands.executeCommand('workbench.view.extension.astraforge-activitybar');
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.clearCache', async () => {
      await ensureLLMManager();
      llmManager.clearCache();
      vscode.window.showInformationMessage('LLM cache cleared');
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('astraforge.showCacheStats', async () => {
      await ensureLLMManager();
      const stats = llmManager.getCacheStats();
      vscode.window.showInformationMessage(
        `Cache Stats - Size: ${stats.cacheSize}, Throttled: ${stats.throttleEntries}`
      );
    })
  );
}

/**
 * Initialize heavy managers only when needed
 */
async function initializeManagers(context: vscode.ExtensionContext) {
  // Auto-init Git if workspace is open (lightweight)
  if (vscode.workspace.workspaceFolders) {
    await ensureGitManager();
    try {
      await gitManager.initRepo(vscode.workspace.workspaceFolders[0].uri.fsPath);
    } catch (error) {
      console.error('Git initialization failed:', error);
    }
  }
}

/**
 * Lazy-load LLM Manager
 */
async function ensureLLMManager() {
  if (!llmManager) {
    const { LLMManager } = await import('./llm/llmManager');
    llmManager = new LLMManager();
  }
  return llmManager;
}

/**
 * Lazy-load Vector DB
 */
async function ensureVectorDB(context: vscode.ExtensionContext) {
  if (!vectorDB) {
    const { VectorDB } = await import('./db/vectorDB');
    vectorDB = new VectorDB(context.extensionUri.fsPath);
    await vectorDB.init();
  }
  return vectorDB;
}

/**
 * Lazy-load Git Manager
 */
async function ensureGitManager() {
  if (!gitManager) {
    const { GitManager } = await import('./git/gitManager');
    gitManager = new GitManager();
  }
  return gitManager;
}

/**
 * Lazy-load Workflow Manager (depends on other managers)
 */
async function ensureWorkflowManager(context: vscode.ExtensionContext) {
  if (!workflowManager) {
    await Promise.all([ensureLLMManager(), ensureVectorDB(context), ensureGitManager()]);

    const { WorkflowManager } = await import('./workflow/workflowManager');
    workflowManager = new WorkflowManager(llmManager, vectorDB, gitManager);
  }
  return workflowManager;
}

export function deactivate() {
  if (vectorDB) {
    vectorDB.close();
  }

  if (llmManager) {
    llmManager.clearCache();
  }
}

import * as vscode from 'vscode';
import { exec } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

export class GitManager {
  private workspacePath: string | undefined;

  async initRepo(path: string) {
    this.workspacePath = path;
    try {
      // Check if git is already initialized
      await execAsync('git status', { cwd: path });
    } catch {
      // Git not initialized, initialize it
      try {
        await execAsync('git init', { cwd: path });
        vscode.window.showInformationMessage('Git repository initialized');
      } catch (initError: any) {
        vscode.window.showErrorMessage(`Failed to initialize Git: ${initError.message}`);
      }
    }
  }

  async commit(message: string) {
    if (!this.workspacePath) {
      vscode.window.showWarningMessage('Git workspace not initialized');
      return;
    }

    try {
      // Add all changes
      await execAsync('git add .', { cwd: this.workspacePath });

      // Check if there are changes to commit
      const { stdout: status } = await execAsync('git status --porcelain', {
        cwd: this.workspacePath,
      });

      if (status.trim()) {
        // There are changes to commit
        await execAsync(`git commit -m "${message}"`, { cwd: this.workspacePath });
        vscode.window.showInformationMessage(`Committed: ${message}`);
      } else {
        vscode.window.showInformationMessage('No changes to commit');
      }
    } catch (error: any) {
      // Handle case where git user is not configured
      if (error.message.includes('user.email') || error.message.includes('user.name')) {
        try {
          await execAsync('git config user.email "astraforge@example.com"', {
            cwd: this.workspacePath,
          });
          await execAsync('git config user.name "AstraForge"', { cwd: this.workspacePath });
          // Retry commit
          await execAsync(`git commit -m "${message}"`, { cwd: this.workspacePath });
          vscode.window.showInformationMessage(`Committed: ${message}`);
        } catch (retryError: any) {
          vscode.window.showErrorMessage(`Git commit failed: ${retryError.message}`);
        }
      } else {
        vscode.window.showErrorMessage(`Git commit failed: ${error.message}`);
      }
    }
  }

  async getStatus(): Promise<string> {
    if (!this.workspacePath) {
      return 'Git workspace not initialized';
    }

    try {
      const { stdout } = await execAsync('git status --short', { cwd: this.workspacePath });
      return stdout;
    } catch (error: any) {
      return `Git status failed: ${error.message}`;
    }
  }
}

/**
 * LLM Provider Interfaces for AstraForge
 * Provides clean abstraction for different LLM providers
 */

export interface LLMConfig {
  provider: 'OpenAI' | 'Anthropic' | 'xAI' | 'OpenRouter';
  key: string;
  model: string;
  role: 'primary' | 'secondary';
  maxTokens?: number;
  temperature?: number;
}

export interface LLMResponse {
  content: string;
  usage?: {
    promptTokens: number;
    completionTokens: number;
    totalTokens: number;
  };
  metadata?: Record<string, any>;
}

export interface LLMProvider {
  /**
   * Query the LLM with a prompt
   * @param prompt The input prompt
   * @param config Provider configuration
   * @returns Promise resolving to LLM response
   */
  query(prompt: string, config: LLMConfig): Promise<LLMResponse>;

  /**
   * Validate API key and configuration
   * @param config Provider configuration
   * @returns Promise resolving to validation result
   */
  validateConfig(config: LLMConfig): Promise<boolean>;

  /**
   * Get available models for this provider
   * @param config Provider configuration
   * @returns Promise resolving to available models
   */
  getAvailableModels(config: LLMConfig): Promise<string[]>;
}

export interface VoteResult {
  option: string;
  votes: number;
  confidence: number;
}

export interface ConferenceResult {
  finalResult: string;
  discussionHistory: string[];
  consensus: number; // 0-1 scale
}

/**
 * Refactored LLM Manager with modular provider architecture
 * Supports parallel requests, caching, and clean provider abstraction
 */

import * as vscode from 'vscode';
import { LLMConfig, LLMProvider, LLMResponse, VoteResult, ConferenceResult } from './interfaces';
import { createProvider } from './providers';
import { LLMCache } from './cache';

export class LLMManager {
  private panel: LLMConfig[] = [];
  private providers = new Map<string, LLMProvider>();
  private cache: LLMCache;
  private readonly maxConcurrentRequests: number;

  constructor() {
    this.panel = vscode.workspace.getConfiguration('astraforge').get('llmPanel', []);
    this.cache = new LLMCache(
      3600, // 1 hour TTL
      60, // 60 requests per minute
      60000 // 1 minute window
    );
    this.maxConcurrentRequests = vscode.workspace
      .getConfiguration('astraforge')
      .get('maxConcurrentRequests', 3);

    this.initializeProviders();
  }

  /**
   * Initialize providers for all configured LLMs
   */
  private initializeProviders(): void {
    for (const config of this.panel) {
      if (!this.providers.has(config.provider)) {
        try {
          const provider = createProvider(config.provider);
          this.providers.set(config.provider, provider);
        } catch (error) {
          console.error(`Failed to initialize provider ${config.provider}:`, error);
        }
      }
    }
  }

  /**
   * Query a specific LLM by index with caching and error handling
   */
  async queryLLM(index: number, prompt: string): Promise<string> {
    const config = this.panel[index];
    if (!config) {
      return 'No LLM configured at index ' + index;
    }

    // Check cache first
    const cachedResponse = this.cache.get(prompt, config.provider, config.model);
    if (cachedResponse) {
      return cachedResponse.response;
    }

    // Check throttling
    if (this.cache.isThrottled(config.provider)) {
      return 'Rate limit exceeded. Please try again later.';
    }

    try {
      const provider = this.providers.get(config.provider);
      if (!provider) {
        throw new Error(`Provider ${config.provider} not initialized`);
      }

      const response = await provider.query(prompt, config);

      // Cache the response
      this.cache.set(prompt, config.provider, config.model, response.content, response.usage);

      return response.content;
    } catch (error: any) {
      vscode.window.showErrorMessage(`LLM query failed: ${error.message}. Falling back...`);

      // Fallback to primary LLM
      if (index !== 0) {
        return this.queryLLM(0, prompt);
      }

      return `Error: ${error.message}`;
    }
  }

  /**
   * Parallel voting system with improved accuracy and fuzzy matching
   */
  async voteOnDecision(prompt: string, options: string[]): Promise<string> {
    if (this.panel.length === 0 || options.length === 0) {
      return options[0] || 'No options provided';
    }

    const votes = new Map<string, number>(options.map(opt => [opt, 0]));

    // Enhanced voting prompt
    const votePrompt = `${prompt}

Please vote on ONE of these options: ${options.join(', ')}
Respond with ONLY the option you choose, exactly as written.`;

    // Create voting promises with concurrency limit
    const votePromises = this.panel.map(async (_, i) => {
      try {
        const response = await this.queryLLM(i, votePrompt);
        return { response, success: true, index: i };
      } catch {
        return { response: options[0], success: false, index: i };
      }
    });

    // Execute with controlled concurrency
    const results = await this.executeWithConcurrencyLimit(votePromises);

    // Process votes with fuzzy matching
    results.forEach(result => {
      const response = result.response.toLowerCase().trim();
      const voted = options.find(
        opt =>
          response.includes(opt.toLowerCase()) ||
          opt.toLowerCase().includes(response) ||
          this.calculateSimilarity(response, opt.toLowerCase()) > 0.7
      );

      if (voted) {
        votes.set(voted, (votes.get(voted) || 0) + 1);
      }
    });

    // Find majority winner with tie-breaking
    let max = 0;
    let winner = options[0];
    votes.forEach((count, opt) => {
      if (count > max || (count === max && opt === options[0])) {
        max = count;
        winner = opt;
      }
    });

    // Enhanced logging for audit trail
    const voteResults = Array.from(votes.entries()).map(([option, count]) => ({ option, count }));
    console.log(
      `Vote results for "${prompt.substring(0, 50)}...": ${JSON.stringify(voteResults)}, Winner: ${winner}`
    );

    return winner;
  }

  /**
   * Conference system for collaborative discussion
   */
  async conference(prompt: string): Promise<string> {
    if (this.panel.length === 0) {
      return 'No LLMs configured for conference';
    }

    let discussion = prompt;
    const discussionHistory: string[] = [prompt];

    // Sequential discussion with each LLM
    for (let i = 0; i < this.panel.length; i++) {
      const config = this.panel[i];
      const response = await this.queryLLM(i, discussion);
      const contribution = `\nLLM ${i + 1} (${config.role} - ${config.provider}): ${response}`;
      discussion += contribution;
      discussionHistory.push(contribution);
    }

    return discussion;
  }

  /**
   * Validate all configured LLM providers
   */
  async validateAllConfigurations(): Promise<Record<string, boolean>> {
    const results: Record<string, boolean> = {};

    const validationPromises = this.panel.map(async (config, index) => {
      const provider = this.providers.get(config.provider);
      if (!provider) {
        return { index, valid: false };
      }

      try {
        const valid = await provider.validateConfig(config);
        return { index, valid };
      } catch {
        return { index, valid: false };
      }
    });

    const validationResults = await Promise.all(validationPromises);

    validationResults.forEach(result => {
      const config = this.panel[result.index];
      results[`${config.provider}-${result.index}`] = result.valid;
    });

    return results;
  }

  /**
   * Get available models for all providers
   */
  async getAvailableModels(): Promise<Record<string, string[]>> {
    const models: Record<string, string[]> = {};

    for (const [providerName, provider] of this.providers.entries()) {
      const config = this.panel.find(c => c.provider === providerName);
      if (config) {
        try {
          models[providerName] = await provider.getAvailableModels(config);
        } catch {
          models[providerName] = [];
        }
      }
    }

    return models;
  }

  /**
   * Execute promises with concurrency limit
   */
  private async executeWithConcurrencyLimit<T>(promises: Promise<T>[]): Promise<T[]> {
    const results: T[] = [];
    const executing: Promise<any>[] = [];

    for (const promise of promises) {
      const p = promise.then(result => {
        results.push(result);
        executing.splice(executing.indexOf(p), 1);
        return result;
      });

      executing.push(p);

      if (executing.length >= this.maxConcurrentRequests) {
        await Promise.race(executing);
      }
    }

    await Promise.all(executing);
    return results;
  }

  /**
   * Calculate string similarity for fuzzy matching
   */
  private calculateSimilarity(str1: string, str2: string): number {
    const maxLength = Math.max(str1.length, str2.length);
    if (maxLength === 0) return 1;

    const distance = this.levenshteinDistance(str1, str2);
    return (maxLength - distance) / maxLength;
  }

  /**
   * Calculate Levenshtein distance between two strings
   */
  private levenshteinDistance(str1: string, str2: string): number {
    const matrix = Array(str2.length + 1)
      .fill(null)
      .map(() => Array(str1.length + 1).fill(null));

    for (let i = 0; i <= str1.length; i++) matrix[0][i] = i;
    for (let j = 0; j <= str2.length; j++) matrix[j][0] = j;

    for (let j = 1; j <= str2.length; j++) {
      for (let i = 1; i <= str1.length; i++) {
        const indicator = str1[i - 1] === str2[j - 1] ? 0 : 1;
        matrix[j][i] = Math.min(
          matrix[j][i - 1] + 1,
          matrix[j - 1][i] + 1,
          matrix[j - 1][i - 1] + indicator
        );
      }
    }

    return matrix[str2.length][str1.length];
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): ReturnType<LLMCache['getStats']> {
    return this.cache.getStats();
  }

  /**
   * Clear cache
   */
  clearCache(): void {
    this.cache.clear();
  }
}

/**
 * Anthropic Provider Implementation
 */

import { BaseLLMProvider } from './baseProvider';
import { LLMConfig, LLMResponse } from '../interfaces';

export class AnthropicProvider extends BaseLLMProvider {
  private readonly baseUrl = 'https://api.anthropic.com/v1';

  async query(prompt: string, config: LLMConfig): Promise<LLMResponse> {
    const sanitizedPrompt = this.sanitizePrompt(prompt);

    const response = await this.makeRequest(
      `${this.baseUrl}/messages`,
      {
        model: config.model,
        max_tokens: config.maxTokens || 1000,
        messages: [{ role: 'user', content: sanitizedPrompt }],
        temperature: config.temperature || 0.7,
      },
      {
        'x-api-key': config.key,
        'Content-Type': 'application/json',
        'anthropic-version': '2023-06-01',
      }
    );

    return {
      content: response.data.content[0].text,
      usage: this.extractUsage(response.data),
      metadata: {
        model: response.data.model,
        stopReason: response.data.stop_reason,
      },
    };
  }

  async validateConfig(config: LLMConfig): Promise<boolean> {
    try {
      // Anthropic doesn't have a models endpoint, so we test with a minimal query
      await this.query('Hi', config);
      return true;
    } catch {
      return false;
    }
  }

  async getAvailableModels(config: LLMConfig): Promise<string[]> {
    // Anthropic doesn't provide a models endpoint, return known models
    return [
      'claude-3-opus-20240229',
      'claude-3-sonnet-20240229',
      'claude-3-haiku-20240307',
      'claude-2.1',
      'claude-2.0',
    ];
  }
}

/**
 * Base LLM Provider Implementation
 * Provides common functionality for all LLM providers
 */

import axios, { AxiosResponse } from 'axios';
import { LLMProvider, LLMConfig, LLMResponse } from '../interfaces';

export abstract class BaseLLMProvider implements LLMProvider {
  protected readonly timeout: number = 30000;
  protected readonly maxRetries: number = 3;

  abstract query(prompt: string, config: LLMConfig): Promise<LLMResponse>;
  abstract validateConfig(config: LLMConfig): Promise<boolean>;
  abstract getAvailableModels(config: LLMConfig): Promise<string[]>;

  /**
   * Make HTTP request with retry logic
   */
  protected async makeRequest(
    url: string,
    data: any,
    headers: Record<string, string>,
    retries: number = this.maxRetries
  ): Promise<AxiosResponse> {
    try {
      return await axios.post(url, data, {
        headers,
        timeout: this.timeout,
      });
    } catch (error: any) {
      if (retries > 0 && this.isRetryableError(error)) {
        await this.delay(1000 * (this.maxRetries - retries + 1));
        return this.makeRequest(url, data, headers, retries - 1);
      }
      throw error;
    }
  }

  /**
   * Check if error is retryable
   */
  protected isRetryableError(error: any): boolean {
    return (
      error.code === 'ECONNRESET' ||
      error.code === 'ETIMEDOUT' ||
      (error.response && [429, 502, 503, 504].includes(error.response.status))
    );
  }

  /**
   * Delay execution
   */
  protected delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Sanitize input prompt
   */
  protected sanitizePrompt(prompt: string): string {
    return prompt.trim().replace(/[\x00-\x1F\x7F]/g, '');
  }

  /**
   * Extract usage information from response
   */
  protected extractUsage(response: any): LLMResponse['usage'] {
    if (response.usage) {
      return {
        promptTokens: response.usage.prompt_tokens || 0,
        completionTokens: response.usage.completion_tokens || 0,
        totalTokens: response.usage.total_tokens || 0,
      };
    }
    return undefined;
  }
}

/**
 * OpenAI Provider Implementation
 */

import { BaseLLMProvider } from './baseProvider';
import { LLMConfig, LLMResponse } from '../interfaces';

export class OpenAIProvider extends BaseLLMProvider {
  private readonly baseUrl = 'https://api.openai.com/v1';

  async query(prompt: string, config: LLMConfig): Promise<LLMResponse> {
    const sanitizedPrompt = this.sanitizePrompt(prompt);

    const response = await this.makeRequest(
      `${this.baseUrl}/chat/completions`,
      {
        model: config.model,
        messages: [{ role: 'user', content: sanitizedPrompt }],
        max_tokens: config.maxTokens || 1000,
        temperature: config.temperature || 0.7,
      },
      {
        Authorization: `Bearer ${config.key}`,
        'Content-Type': 'application/json',
      }
    );

    return {
      content: response.data.choices[0].message.content,
      usage: this.extractUsage(response.data),
      metadata: {
        model: response.data.model,
        finishReason: response.data.choices[0].finish_reason,
      },
    };
  }

  async validateConfig(config: LLMConfig): Promise<boolean> {
    try {
      const response = await this.makeRequest(`${this.baseUrl}/models`, null, {
        Authorization: `Bearer ${config.key}`,
        'Content-Type': 'application/json',
      });
      return response.status === 200;
    } catch {
      return false;
    }
  }

  async getAvailableModels(config: LLMConfig): Promise<string[]> {
    try {
      const response = await this.makeRequest(`${this.baseUrl}/models`, null, {
        Authorization: `Bearer ${config.key}`,
        'Content-Type': 'application/json',
      });

      return response.data.data
        .filter((model: any) => model.id.includes('gpt'))
        .map((model: any) => model.id)
        .sort();
    } catch {
      return ['gpt-4', 'gpt-3.5-turbo']; // fallback
    }
  }
}

