import * as vscode from 'vscode';
import { WorkflowManager } from '../workflow/workflowManager';

export class ProjectIgnitionProvider implements vscode.WebviewViewProvider {
  public static readonly viewType = 'astraforge.projectIgnition';
  private _view?: vscode.Webview;

  constructor(
    private readonly _extensionUri: vscode.Uri,
    private readonly _workflowManager: WorkflowManager
  ) {
    // Initialize provider with extension URI and workflow manager
    // These parameters are used in the class methods
  }

  public resolveWebviewView(
    webviewView: vscode.WebviewView,
    _context: vscode.WebviewViewResolveContext,
    _token: vscode.CancellationToken
  ) {
    // _context and _token are required by interface but not used in this implementation
    this._view = webviewView.webview;

    webviewView.webview.options = {
      enableScripts: true,
      localResourceRoots: [this._extensionUri],
    };

    webviewView.webview.html = this._getHtmlForWebview(webviewView.webview);

    webviewView.webview.onDidReceiveMessage(async data => {
      if (data.type === 'submitIdea') {
        this._workflowManager.startWorkflow(data.idea, data.option);
      }
    });
  }

  private _getHtmlForWebview(webview: vscode.Webview) {
    const scriptUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'ignition.js')
    );
    const styleUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'styles.css')
    );

    return `<!DOCTYPE html>
<html lang="en">
<head>
  <link href="${styleUri}" rel="stylesheet">
</head>
<body>
  <h1>Project Ignition</h1>
  <textarea id="ideaInput" placeholder="Enter your project idea..."></textarea>
  <select id="promptOption">
    <option value="direct">Direct Submit</option>
    <option value="letPanelDecide">Let the panel decide</option>
    <option value="custom">Custom refinements</option>
  </select>
  <div id="customBox" style="display:none;">
    <textarea id="customText"></textarea>
  </div>
  <button onclick="submitIdea()">Submit</button>
  <div id="progressTracker"></div>
  <script src="${scriptUri}"></script>
</body>
</html>`;
  }
}

import * as vscode from 'vscode';

export class SetupWizardProvider implements vscode.WebviewViewProvider {
  public static readonly viewType = 'astraforge.setupWizard';
  private _view?: vscode.Webview;

  constructor(private readonly _extensionUri: vscode.Uri) {
    // Initialize setup wizard with extension URI
    // _extensionUri is used in _getHtmlForWebview method
  }

  public resolveWebviewView(
    webviewView: vscode.WebviewView,
    _context: vscode.WebviewViewResolveContext,
    _token: vscode.CancellationToken
  ) {
    // _context and _token are required by interface but not used in this implementation
    this._view = webviewView.webview;

    webviewView.webview.options = {
      enableScripts: true,
      localResourceRoots: [this._extensionUri],
    };

    webviewView.webview.html = this._getHtmlForWebview(webviewView.webview);

    webviewView.webview.onDidReceiveMessage(async data => {
      switch (data.type) {
        case 'savePanel':
          await vscode.workspace
            .getConfiguration('astraforge')
            .update('llmPanel', data.panel, true);
          vscode.window.showInformationMessage('LLM Panel configured! Ready for ignition.');
          break;
      }
    });
  }

  private _getHtmlForWebview(webview: vscode.Webview) {
    const scriptUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'setup.js')
    );
    const styleUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'styles.css')
    );

    // Basic HTML with form for 3/5 LLMs, providers dropdown (OpenAI, Anthropic, xAI, OpenRouter), API key inputs
    return `<!DOCTYPE html>
      <html lang="en">
      <head><link href="${styleUri}" rel="stylesheet"></head>
      <body>
        <h1>Setup LLM Panel</h1>
        <select id="panelSize">
          <option value="3">3 LLMs</option>
          <option value="5">5 LLMs</option>
        </select>
        <div id="llmForms"></div>
        <button onclick="savePanel()">Save Configuration</button>
        <script src="${scriptUri}"></script>
      </body>
      </html>`;
  }
}

/**
 * Reinforcement Learning module for adaptive workflow optimization
 * Uses Q-learning to optimize phase sequencing and decision making
 */

interface WorkflowState {
  currentPhase: string;
  projectComplexity: number; // 0-1 scale
  userSatisfaction: number; // 0-1 scale from feedback
  errorRate: number; // 0-1 scale
  timeSpent: number; // normalized time
}

interface WorkflowAction {
  type: 'continue' | 'skip' | 'repeat' | 'branch' | 'optimize';
  target?: string; // target phase or optimization
  confidence: number; // 0-1 scale
}

interface QTableEntry {
  state: string; // serialized state
  action: string; // serialized action
  qValue: number;
  visits: number;
}

export class AdaptiveWorkflowRL {
  private qTable: Map<string, Map<string, QTableEntry>> = new Map();
  private learningRate = 0.1;
  private discountFactor = 0.9;
  private explorationRate = 0.1;
  private minExplorationRate = 0.01;
  private explorationDecay = 0.995;

  private readonly phases = ['Planning', 'Prototyping', 'Testing', 'Deployment'];
  private readonly actions: WorkflowAction[] = [
    { type: 'continue', confidence: 1.0 },
    { type: 'skip', confidence: 0.8 },
    { type: 'repeat', confidence: 0.9 },
    { type: 'branch', target: 'Testing', confidence: 0.7 },
    { type: 'optimize', confidence: 0.6 },
  ];

  constructor() {
    this.loadQTable();
  }

  /**
   * Get the best action for a given state using epsilon-greedy policy
   */
  getBestAction(state: WorkflowState): WorkflowAction {
    const stateKey = this.serializeState(state);

    // Epsilon-greedy exploration
    if (Math.random() < this.explorationRate) {
      return this.getRandomAction();
    }

    // Get best known action
    const stateActions = this.qTable.get(stateKey);
    if (!stateActions || stateActions.size === 0) {
      return this.getRandomAction();
    }

    let bestAction: WorkflowAction = this.actions[0];
    let bestQValue = -Infinity;

    for (const [actionKey, entry] of stateActions) {
      if (entry.qValue > bestQValue) {
        bestQValue = entry.qValue;
        bestAction = this.deserializeAction(actionKey);
      }
    }

    console.log(
      `RL: Selected action ${bestAction.type} for state ${stateKey} (Q-value: ${bestQValue.toFixed(3)})`
    );
    return bestAction;
  }

  /**
   * Update Q-value based on reward received
   */
  updateQValue(
    state: WorkflowState,
    action: WorkflowAction,
    reward: number,
    nextState: WorkflowState
  ): void {
    const stateKey = this.serializeState(state);
    const actionKey = this.serializeAction(action);
    const nextStateKey = this.serializeState(nextState);

    // Initialize state in Q-table if not exists
    if (!this.qTable.has(stateKey)) {
      this.qTable.set(stateKey, new Map());
    }

    const stateActions = this.qTable.get(stateKey)!;

    // Initialize action if not exists
    if (!stateActions.has(actionKey)) {
      stateActions.set(actionKey, {
        state: stateKey,
        action: actionKey,
        qValue: 0,
        visits: 0,
      });
    }

    const entry = stateActions.get(actionKey)!;

    // Get max Q-value for next state
    const nextStateActions = this.qTable.get(nextStateKey);
    let maxNextQValue = 0;
    if (nextStateActions) {
      for (const nextEntry of nextStateActions.values()) {
        maxNextQValue = Math.max(maxNextQValue, nextEntry.qValue);
      }
    }

    // Q-learning update rule
    const oldQValue = entry.qValue;
    const newQValue =
      oldQValue + this.learningRate * (reward + this.discountFactor * maxNextQValue - oldQValue);

    entry.qValue = newQValue;
    entry.visits++;

    // Decay exploration rate
    this.explorationRate = Math.max(
      this.minExplorationRate,
      this.explorationRate * this.explorationDecay
    );

    console.log(
      `RL: Updated Q(${stateKey}, ${actionKey}) from ${oldQValue.toFixed(3)} to ${newQValue.toFixed(3)} (reward: ${reward.toFixed(3)})`
    );

    // Save updated Q-table periodically
    if (entry.visits % 10 === 0) {
      this.saveQTable();
    }
  }

  /**
   * Calculate reward based on workflow performance
   */
  calculateReward(
    oldState: WorkflowState,
    action: WorkflowAction,
    newState: WorkflowState,
    phaseSuccess: boolean,
    userFeedback?: number
  ): number {
    let reward = 0;

    // Base reward for phase success
    if (phaseSuccess) {
      reward += 1.0;
    } else {
      reward -= 0.5;
    }

    // Reward for improving user satisfaction
    const satisfactionImprovement = newState.userSatisfaction - oldState.userSatisfaction;
    reward += satisfactionImprovement * 2.0;

    // Penalty for increasing error rate
    const errorIncrease = newState.errorRate - oldState.errorRate;
    reward -= errorIncrease * 1.5;

    // Reward for efficient time usage
    if (newState.timeSpent < oldState.timeSpent && phaseSuccess) {
      reward += 0.3;
    }

    // Action-specific rewards
    switch (action.type) {
      case 'continue':
        if (phaseSuccess && newState.userSatisfaction > 0.7) {
          reward += 0.2;
        }
        break;
      case 'skip':
        if (oldState.projectComplexity < 0.3 && phaseSuccess) {
          reward += 0.4; // Good to skip for simple projects
        } else {
          reward -= 0.3; // Risky for complex projects
        }
        break;
      case 'repeat':
        if (oldState.errorRate > 0.5 && newState.errorRate < oldState.errorRate) {
          reward += 0.5; // Good decision to repeat when there were errors
        }
        break;
      case 'optimize':
        if (newState.timeSpent < oldState.timeSpent * 0.8) {
          reward += 0.6; // Significant time improvement
        }
        break;
    }

    // User feedback integration
    if (userFeedback !== undefined) {
      reward += (userFeedback - 0.5) * 2.0; // Scale 0-1 feedback to -1 to +1
    }

    return Math.max(-2.0, Math.min(2.0, reward)); // Clamp reward between -2 and +2
  }

  private getRandomAction(): WorkflowAction {
    return this.actions[Math.floor(Math.random() * this.actions.length)];
  }

  private serializeState(state: WorkflowState): string {
    return JSON.stringify({
      phase: state.currentPhase,
      complexity: Math.round(state.projectComplexity * 10) / 10,
      satisfaction: Math.round(state.userSatisfaction * 10) / 10,
      errorRate: Math.round(state.errorRate * 10) / 10,
      timeNorm: Math.round(state.timeSpent * 10) / 10,
    });
  }

  private serializeAction(action: WorkflowAction): string {
    return JSON.stringify({
      type: action.type,
      target: action.target || null,
    });
  }

  private deserializeAction(actionKey: string): WorkflowAction {
    const parsed = JSON.parse(actionKey);
    return {
      type: parsed.type,
      target: parsed.target,
      confidence: 0.8, // Default confidence for deserialized actions
    };
  }

  private saveQTable(): void {
    try {
      const serialized = Array.from(this.qTable.entries()).map(([stateKey, actions]) => ({
        state: stateKey,
        actions: Array.from(actions.entries()).map(([actionKey, entry]) => ({
          action: actionKey,
          qValue: entry.qValue,
          visits: entry.visits,
        })),
      }));

      // Store in memory for now - in VS Code extension, this would use vscode.ExtensionContext.globalState
      (global as any).astraforge_qtable = serialized;
      console.log(`RL: Saved Q-table with ${this.qTable.size} states`);
    } catch (error) {
      console.warn('RL: Failed to save Q-table:', error);
    }
  }

  private loadQTable(): void {
    try {
      const saved = (global as any).astraforge_qtable;
      if (!saved) return;

      const serialized = saved;
      for (const stateData of serialized) {
        const stateActions = new Map<string, QTableEntry>();
        for (const actionData of stateData.actions) {
          stateActions.set(actionData.action, {
            state: stateData.state,
            action: actionData.action,
            qValue: actionData.qValue,
            visits: actionData.visits,
          });
        }
        this.qTable.set(stateData.state, stateActions);
      }

      console.log(`RL: Loaded Q-table with ${this.qTable.size} states`);
    } catch (error) {
      console.warn('RL: Failed to load Q-table:', error);
    }
  }

  /**
   * Get learning statistics for monitoring
   */
  getStats(): { totalStates: number; totalActions: number; explorationRate: number } {
    let totalActions = 0;
    for (const actions of this.qTable.values()) {
      totalActions += actions.size;
    }

    return {
      totalStates: this.qTable.size,
      totalActions,
      explorationRate: this.explorationRate,
    };
  }
}

/**
 * Real-time collaboration server using Socket.IO
 * Handles multi-agent isolation and communication
 */

import { Server } from 'socket.io';
import { createServer } from 'http';
import * as vscode from 'vscode';

interface AgentSession {
  id: string;
  type: 'llm' | 'user' | 'system';
  name: string;
  capabilities: string[];
  status: 'active' | 'busy' | 'idle' | 'error';
  workspaceId: string;
}

interface CollaborationMessage {
  type: 'code_change' | 'discussion' | 'decision' | 'status_update';
  sender: string;
  content: any;
  timestamp: number;
  targetAgents?: string[];
}

export class CollaborationServer {
  private server: any;
  private io: Server;
  private port: number;
  private agents: Map<string, AgentSession> = new Map();
  private workspaces: Map<string, Set<string>> = new Map(); // workspace -> agent IDs
  private messageHistory: Map<string, CollaborationMessage[]> = new Map(); // workspace -> messages

  constructor(port: number = 3000) {
    this.port = port;
    this.server = createServer();
    this.io = new Server(this.server, {
      cors: {
        origin: '*',
        methods: ['GET', 'POST'],
      },
    });

    this.setupEventHandlers();
  }

  async start(): Promise<void> {
    return new Promise((resolve, reject) => {
      this.server.listen(this.port, (error: any) => {
        if (error) {
          reject(error);
        } else {
          console.log(`Collaboration server started on port ${this.port}`);
          resolve();
        }
      });
    });
  }

  async stop(): Promise<void> {
    return new Promise(resolve => {
      this.server.close(() => {
        console.log('Collaboration server stopped');
        resolve();
      });
    });
  }

  private setupEventHandlers(): void {
    this.io.on('connection', socket => {
      console.log(`New connection: ${socket.id}`);

      // Agent registration
      socket.on('register_agent', (agentData: Partial<AgentSession>) => {
        this.registerAgent(socket, agentData);
      });

      // Join workspace
      socket.on('join_workspace', (workspaceId: string) => {
        this.joinWorkspace(socket, workspaceId);
      });

      // Handle collaboration messages
      socket.on('collaboration_message', (message: CollaborationMessage) => {
        this.handleMessage(socket, message);
      });

      // Request agent isolation
      socket.on('request_isolation', (taskData: any) => {
        this.createIsolatedAgent(socket, taskData);
      });

      // Status updates
      socket.on('status_update', (status: string) => {
        this.updateAgentStatus(socket, status);
      });

      // Disconnection
      socket.on('disconnect', () => {
        this.handleDisconnection(socket);
      });
    });
  }

  private registerAgent(socket: any, agentData: Partial<AgentSession>): void {
    const agent: AgentSession = {
      id: socket.id,
      type: agentData.type || 'system',
      name: agentData.name || `Agent-${socket.id.substring(0, 8)}`,
      capabilities: agentData.capabilities || [],
      status: 'active',
      workspaceId: agentData.workspaceId || 'default',
    };

    this.agents.set(socket.id, agent);
    socket.join(agent.workspaceId);

    // Add to workspace
    if (!this.workspaces.has(agent.workspaceId)) {
      this.workspaces.set(agent.workspaceId, new Set());
    }
    this.workspaces.get(agent.workspaceId)!.add(socket.id);

    // Notify other agents in workspace
    socket.to(agent.workspaceId).emit('agent_joined', {
      agent: agent,
      timestamp: Date.now(),
    });

    socket.emit('registration_complete', {
      agent: agent,
      workspace_agents: this.getWorkspaceAgents(agent.workspaceId),
    });

    console.log(
      `Agent registered: ${agent.name} (${agent.type}) in workspace ${agent.workspaceId}`
    );
  }

  private joinWorkspace(socket: any, workspaceId: string): void {
    const agent = this.agents.get(socket.id);
    if (!agent) return;

    // Leave current workspace
    socket.leave(agent.workspaceId);
    this.workspaces.get(agent.workspaceId)?.delete(socket.id);

    // Join new workspace
    agent.workspaceId = workspaceId;
    socket.join(workspaceId);

    if (!this.workspaces.has(workspaceId)) {
      this.workspaces.set(workspaceId, new Set());
    }
    this.workspaces.get(workspaceId)!.add(socket.id);

    // Send message history
    const history = this.messageHistory.get(workspaceId) || [];
    socket.emit('message_history', history.slice(-50)); // Last 50 messages

    // Notify workspace
    socket.to(workspaceId).emit('agent_joined', {
      agent: agent,
      timestamp: Date.now(),
    });
  }

  private handleMessage(socket: any, message: CollaborationMessage): void {
    const agent = this.agents.get(socket.id);
    if (!agent) return;

    // Add sender info and timestamp
    message.sender = agent.id;
    message.timestamp = Date.now();

    // Store message in history
    if (!this.messageHistory.has(agent.workspaceId)) {
      this.messageHistory.set(agent.workspaceId, []);
    }
    const history = this.messageHistory.get(agent.workspaceId)!;
    history.push(message);

    // Keep only last 1000 messages
    if (history.length > 1000) {
      history.splice(0, history.length - 1000);
    }

    // Route message based on type and targets
    if (message.targetAgents && message.targetAgents.length > 0) {
      // Send to specific agents
      message.targetAgents.forEach(targetId => {
        socket.to(targetId).emit('collaboration_message', message);
      });
    } else {
      // Broadcast to all agents in workspace
      socket.to(agent.workspaceId).emit('collaboration_message', message);
    }

    // Handle special message types
    this.processMessageType(message, agent);
  }

  private processMessageType(message: CollaborationMessage, sender: AgentSession): void {
    switch (message.type) {
      case 'decision':
        this.handleDecisionMessage(message, sender);
        break;
      case 'code_change':
        this.handleCodeChange(message, sender);
        break;
      case 'status_update':
        this.handleStatusUpdate(message, sender);
        break;
    }
  }

  private handleDecisionMessage(message: CollaborationMessage, sender: AgentSession): void {
    // Implement voting mechanism for multi-agent decisions
    const decision = message.content;

    // Notify VS Code extension about the decision
    vscode.window.showInformationMessage(
      `Agent ${sender.name} made a decision: ${decision.summary || 'Decision pending'}`
    );
  }

  private handleCodeChange(message: CollaborationMessage, sender: AgentSession): void {
    // Handle real-time code synchronization
    const change = message.content;

    // Broadcast to VS Code extension
    this.io.to(sender.workspaceId).emit('code_sync', {
      file: change.file,
      diff: change.diff,
      author: sender.name,
      timestamp: message.timestamp,
    });
  }

  private handleStatusUpdate(message: CollaborationMessage, sender: AgentSession): void {
    const agent = this.agents.get(sender.id);
    if (agent) {
      agent.status = message.content.status || agent.status;

      // Notify workspace about status change
      this.io.to(agent.workspaceId).emit('agent_status_update', {
        agentId: sender.id,
        status: agent.status,
        timestamp: message.timestamp,
      });
    }
  }

  private createIsolatedAgent(socket: any, taskData: any): void {
    // Create isolated environment for heavy tasks
    const isolationId = `isolated_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // Create isolated workspace
    this.workspaces.set(isolationId, new Set([socket.id]));

    // Move agent to isolated workspace temporarily
    const agent = this.agents.get(socket.id);
    if (agent) {
      const originalWorkspace = agent.workspaceId;

      socket.leave(originalWorkspace);
      socket.join(isolationId);

      socket.emit('isolation_created', {
        isolationId,
        originalWorkspace,
        taskData,
        timestamp: Date.now(),
      });

      // Set timeout to return agent to original workspace
      setTimeout(() => {
        socket.leave(isolationId);
        socket.join(originalWorkspace);
        this.workspaces.delete(isolationId);

        socket.emit('isolation_ended', {
          isolationId,
          returnedTo: originalWorkspace,
          timestamp: Date.now(),
        });
      }, taskData.timeout || 30000); // Default 30 seconds
    }
  }

  private updateAgentStatus(socket: any, status: string): void {
    const agent = this.agents.get(socket.id);
    if (agent) {
      agent.status = status as any;

      // Notify workspace
      socket.to(agent.workspaceId).emit('agent_status_update', {
        agentId: socket.id,
        status: status,
        timestamp: Date.now(),
      });
    }
  }

  private handleDisconnection(socket: any): void {
    const agent = this.agents.get(socket.id);
    if (agent) {
      // Remove from workspace
      this.workspaces.get(agent.workspaceId)?.delete(socket.id);

      // Notify other agents
      socket.to(agent.workspaceId).emit('agent_left', {
        agentId: socket.id,
        agentName: agent.name,
        timestamp: Date.now(),
      });

      // Cleanup
      this.agents.delete(socket.id);

      console.log(`Agent disconnected: ${agent.name} (${socket.id})`);
    }
  }

  private getWorkspaceAgents(workspaceId: string): AgentSession[] {
    const agentIds = this.workspaces.get(workspaceId) || new Set();
    const agents: AgentSession[] = [];

    for (const agentId of agentIds) {
      const agent = this.agents.get(agentId);
      if (agent) {
        agents.push(agent);
      }
    }

    return agents;
  }

  // Public API methods for integration with VS Code extension

  broadcastToWorkspace(workspaceId: string, event: string, data: any): void {
    this.io.to(workspaceId).emit(event, data);
  }

  getWorkspaceStats(workspaceId: string): any {
    const agents = this.getWorkspaceAgents(workspaceId);
    const messages = this.messageHistory.get(workspaceId) || [];

    return {
      agentCount: agents.length,
      messageCount: messages.length,
      activeAgents: agents.filter(a => a.status === 'active').length,
      lastActivity: messages.length > 0 ? messages[messages.length - 1].timestamp : null,
    };
  }

  getAllWorkspaces(): string[] {
    return Array.from(this.workspaces.keys());
  }
}

import { LLMManager } from '../llm/llmManager';
import { VectorDB } from '../db/vectorDB';
import { GeneratedSpec } from './specGenerator';
import * as fs from 'fs';
import * as path from 'path';
import { logger } from '../utils/logger';

export interface TechnicalPlan {
  summary: string;
  technicalContext: TechnicalContext;
  constitutionCheck: ConstitutionCheck;
  projectStructure: ProjectStructure;
  researchTasks: ResearchTask[];
  designPhase: DesignPhase;
  taskPlanningApproach: string;
  content: string;
}

export interface TechnicalContext {
  language: string;
  primaryDependencies: string[];
  storage: string;
  testing: string;
  targetPlatform: string;
  projectType: 'single' | 'web' | 'mobile';
  performanceGoals: string[];
  constraints: string[];
  scale: string;
}

export interface ConstitutionCheck {
  simplicity: {
    projectCount: number;
    usingFrameworkDirectly: boolean;
    singleDataModel: boolean;
    avoidingPatterns: boolean;
  };
  architecture: {
    everyFeatureAsLibrary: boolean;
    libraries: Array<{ name: string; purpose: string }>;
    cliPerLibrary: string[];
    libraryDocsPlanned: boolean;
  };
  testing: {
    redGreenRefactorEnforced: boolean;
    gitCommitsShowTestsFirst: boolean;
    orderFollowed: boolean;
    realDependenciesUsed: boolean;
    integrationTestsPlanned: boolean;
  };
  observability: {
    structuredLoggingIncluded: boolean;
    frontendToBackendLogs: boolean;
    errorContextSufficient: boolean;
  };
  versioning: {
    versionAssigned: string;
    buildIncrementsPlanned: boolean;
    breakingChangesHandled: boolean;
  };
  violations: string[];
}

export interface ProjectStructure {
  type: 'single' | 'web' | 'mobile';
  directories: string[];
  description: string;
}

export interface ResearchTask {
  id: string;
  description: string;
  rationale: string;
  alternatives: string[];
  decision?: string;
}

export interface DesignPhase {
  dataModel: string;
  apiContracts: string[];
  contractTests: string[];
  testScenarios: string[];
  quickstart: string;
}

export class PlanGenerator {
  private llmManager: LLMManager;
  private vectorDB: VectorDB;
  private planTemplate: string = '';

  constructor(llmManager: LLMManager, vectorDB: VectorDB) {
    this.llmManager = llmManager;
    this.vectorDB = vectorDB;
    this.loadPlanTemplate();
  }

  private loadPlanTemplate(): void {
    const templatePath = path.join(__dirname, '../../templates/plan-template.md');
    try {
      this.planTemplate = fs.readFileSync(templatePath, 'utf8');
    } catch (error) {
      logger.error('Failed to load plan template:', error);
      this.planTemplate = this.getDefaultPlanTemplate();
    }
  }

  private getDefaultPlanTemplate(): string {
    return `# Implementation Plan: {{FEATURE_NAME}}

**Branch**: \`{{BRANCH_NAME}}\` | **Date**: {{DATE}} | **Spec**: {{SPEC_LINK}}
**Input**: Feature specification from \`/specs/{{FEATURE_DIR}}/spec.md\`

## Summary
{{SUMMARY}}

## Technical Context
**Language/Version**: {{LANGUAGE}}
**Primary Dependencies**: {{DEPENDENCIES}}
**Storage**: {{STORAGE}}
**Testing**: {{TESTING}}
**Target Platform**: {{TARGET_PLATFORM}}
**Project Type**: {{PROJECT_TYPE}}
**Performance Goals**: {{PERFORMANCE_GOALS}}
**Constraints**: {{CONSTRAINTS}}
**Scale/Scope**: {{SCALE}}

## Constitution Check
{{CONSTITUTION_CHECK}}

## Project Structure
{{PROJECT_STRUCTURE}}

## Phase 0: Outline & Research
{{RESEARCH_PHASE}}

## Phase 1: Design & Contracts
{{DESIGN_PHASE}}

## Phase 2: Task Planning Approach
{{TASK_PLANNING}}

## Progress Tracking
{{PROGRESS_TRACKING}}
`;
  }

  public async generatePlan(spec: GeneratedSpec, technicalRequirements?: Partial<TechnicalContext>): Promise<TechnicalPlan> {
    logger.info('üîß Generating technical implementation plan...');

    // Phase 0: Research and technical context
    const technicalContext = await this.determineTechnicalContext(spec, technicalRequirements);
    const researchTasks = await this.generateResearchTasks(spec, technicalContext);

    // Constitution check
    const constitutionCheck = await this.performConstitutionCheck(spec, technicalContext);

    // Project structure determination
    const projectStructure = await this.determineProjectStructure(technicalContext);

    // Phase 1: Design phase planning
    const designPhase = await this.planDesignPhase(spec, technicalContext);

    // Phase 2: Task planning approach
    const taskPlanningApproach = await this.defineTaskPlanningApproach(spec, designPhase);

    // Generate summary
    const summary = await this.generateSummary(spec, technicalContext);

    // Assemble the plan
    const content = this.assemblePlan({
      spec,
      summary,
      technicalContext,
      constitutionCheck,
      projectStructure,
      researchTasks,
      designPhase,
      taskPlanningApproach
    });

    return {
      summary,
      technicalContext,
      constitutionCheck,
      projectStructure,
      researchTasks,
      designPhase,
      taskPlanningApproach,
      content
    };
  }

  private async determineTechnicalContext(spec: GeneratedSpec, requirements?: Partial<TechnicalContext>): Promise<TechnicalContext> {
    const prompt = `
    Determine the technical context for this feature specification:
    
    Feature: ${spec.title}
    Requirements: ${JSON.stringify(spec.functionalRequirements, null, 2)}
    User Scenarios: ${JSON.stringify(spec.userScenarios, null, 2)}
    Key Entities: ${JSON.stringify(spec.keyEntities, null, 2)}
    
    ${requirements ? `Existing Requirements: ${JSON.stringify(requirements, null, 2)}` : ''}
    
    Determine:
    1. Best programming language and version
    2. Primary dependencies/frameworks
    3. Storage requirements
    4. Testing framework
    5. Target platform
    6. Project type (single/web/mobile)
    7. Performance goals
    8. Constraints
    9. Scale/scope
    
    Consider AstraForge context: This is a VS Code extension environment with Node.js/TypeScript.
    
    Return JSON with technical context structure.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      const context = JSON.parse(response);
      
      // Merge with provided requirements
      return {
        language: requirements?.language || context.language || 'TypeScript 5.1',
        primaryDependencies: requirements?.primaryDependencies || context.primaryDependencies || ['Node.js'],
        storage: requirements?.storage || context.storage || 'File System',
        testing: requirements?.testing || context.testing || 'Jest',
        targetPlatform: requirements?.targetPlatform || context.targetPlatform || 'VS Code Extension',
        projectType: requirements?.projectType || context.projectType || 'single',
        performanceGoals: requirements?.performanceGoals || context.performanceGoals || ['Responsive UI'],
        constraints: requirements?.constraints || context.constraints || ['VS Code API limitations'],
        scale: requirements?.scale || context.scale || 'Single user workspace'
      };
    } catch (error) {
      logger.error('Error determining technical context:', error);
      return {
        language: 'TypeScript 5.1',
        primaryDependencies: ['Node.js', 'VS Code API'],
        storage: 'File System',
        testing: 'Jest',
        targetPlatform: 'VS Code Extension',
        projectType: 'single',
        performanceGoals: ['Responsive UI', '<100ms response time'],
        constraints: ['VS Code API limitations', 'Single workspace context'],
        scale: 'Single user workspace'
      };
    }
  }

  private async generateResearchTasks(spec: GeneratedSpec, context: TechnicalContext): Promise<ResearchTask[]> {
    const prompt = `
    Generate research tasks for this technical plan:
    
    Specification: ${spec.title}
    Technical Context: ${JSON.stringify(context, null, 2)}
    Clarifications Needed: ${JSON.stringify(spec.clarificationNeeded, null, 2)}
    
    Identify areas that need research:
    1. Unknown technologies or best practices
    2. Integration patterns
    3. Performance optimization techniques
    4. Testing strategies
    5. Architecture decisions
    
    For each research task, provide:
    - Unique ID
    - Clear description
    - Rationale (why needed)
    - Alternative approaches to consider
    
    Return JSON array of research tasks.
    `;

    try {
      const response = await this.llmManager.generateResponse('anthropic', prompt);
      return JSON.parse(response);
    } catch (error) {
      logger.error('Error generating research tasks:', error);
      return [
        {
          id: 'R001',
          description: 'Research best practices for VS Code extension development',
          rationale: 'Ensure optimal user experience and performance',
          alternatives: ['Basic implementation', 'Advanced optimization', 'Minimal viable product']
        }
      ];
    }
  }

  private async performConstitutionCheck(spec: GeneratedSpec, context: TechnicalContext): Promise<ConstitutionCheck> {
    const prompt = `
    Perform constitution compliance check for this plan:
    
    Specification: ${spec.title}
    Technical Context: ${JSON.stringify(context, null, 2)}
    Functional Requirements: ${JSON.stringify(spec.functionalRequirements, null, 2)}
    
    Check against AstraForge Constitution:
    
    1. Simplicity:
       - Max 3 projects
       - Using framework directly (no wrapper classes)
       - Single data model (no DTOs unless needed)
       - Avoiding unnecessary patterns
    
    2. Architecture:
       - Every feature as library
       - CLI per library
       - Library documentation planned
    
    3. Testing (NON-NEGOTIABLE):
       - RED-GREEN-Refactor cycle
       - Tests before implementation
       - Contract‚ÜíIntegration‚ÜíE2E‚ÜíUnit order
       - Real dependencies used
       - Integration tests for new libraries
    
    4. Observability:
       - Structured logging
       - Error context
    
    5. Versioning:
       - Version number assigned
       - BUILD increments
       - Breaking changes handled
    
    Return detailed constitution check object with violations.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return JSON.parse(response);
    } catch (error) {
      logger.error('Error performing constitution check:', error);
      return {
        simplicity: {
          projectCount: 1,
          usingFrameworkDirectly: true,
          singleDataModel: true,
          avoidingPatterns: true
        },
        architecture: {
          everyFeatureAsLibrary: true,
          libraries: [{ name: spec.title, purpose: 'Core functionality' }],
          cliPerLibrary: [`${spec.title.toLowerCase()}`],
          libraryDocsPlanned: true
        },
        testing: {
          redGreenRefactorEnforced: true,
          gitCommitsShowTestsFirst: true,
          orderFollowed: true,
          realDependenciesUsed: true,
          integrationTestsPlanned: true
        },
        observability: {
          structuredLoggingIncluded: true,
          frontendToBackendLogs: false,
          errorContextSufficient: true
        },
        versioning: {
          versionAssigned: '1.0.0',
          buildIncrementsPlanned: true,
          breakingChangesHandled: true
        },
        violations: []
      };
    }
  }

  private async determineProjectStructure(context: TechnicalContext): Promise<ProjectStructure> {
    const structures = {
      single: {
        type: 'single' as const,
        directories: [
          'src/',
          'src/models/',
          'src/services/',
          'src/cli/',
          'src/lib/',
          'tests/',
          'tests/contract/',
          'tests/integration/',
          'tests/unit/'
        ],
        description: 'Single project structure for focused functionality'
      },
      web: {
        type: 'web' as const,
        directories: [
          'backend/',
          'backend/src/',
          'backend/src/models/',
          'backend/src/services/',
          'backend/src/api/',
          'backend/tests/',
          'frontend/',
          'frontend/src/',
          'frontend/src/components/',
          'frontend/src/pages/',
          'frontend/src/services/',
          'frontend/tests/'
        ],
        description: 'Web application with separate frontend and backend'
      },
      mobile: {
        type: 'mobile' as const,
        directories: [
          'api/',
          'api/src/',
          'api/tests/',
          'mobile/',
          'mobile/src/',
          'mobile/tests/'
        ],
        description: 'Mobile application with API backend'
      }
    };

    return structures[context.projectType] || structures.single;
  }

  private async planDesignPhase(spec: GeneratedSpec, context: TechnicalContext): Promise<DesignPhase> {
    const prompt = `
    Plan the design phase for this feature:
    
    Specification: ${spec.title}
    Key Entities: ${JSON.stringify(spec.keyEntities, null, 2)}
    Functional Requirements: ${JSON.stringify(spec.functionalRequirements, null, 2)}
    User Scenarios: ${JSON.stringify(spec.userScenarios, null, 2)}
    Technical Context: ${JSON.stringify(context, null, 2)}
    
    Plan:
    1. Data model design (entities, relationships, validation)
    2. API contracts (endpoints, schemas)
    3. Contract tests (one per endpoint)
    4. Test scenarios (from user stories)
    5. Quickstart guide (validation steps)
    
    Return JSON with design phase structure.
    `;

    try {
      const response = await this.llmManager.generateResponse('anthropic', prompt);
      return JSON.parse(response);
    } catch (error) {
      logger.error('Error planning design phase:', error);
      return {
        dataModel: 'Data model to be defined based on key entities',
        apiContracts: ['API contracts to be generated from functional requirements'],
        contractTests: ['Contract tests to be created for each endpoint'],
        testScenarios: ['Integration test scenarios from user stories'],
        quickstart: 'Quickstart guide with validation steps'
      };
    }
  }

  private async defineTaskPlanningApproach(spec: GeneratedSpec, designPhase: DesignPhase): Promise<string> {
    const prompt = `
    Define the task planning approach for this feature:
    
    Specification: ${spec.title}
    Design Phase: ${JSON.stringify(designPhase, null, 2)}
    
    Describe how tasks will be generated:
    1. Task generation strategy
    2. Ordering strategy (TDD, dependencies)
    3. Parallel execution opportunities
    4. Estimated task count
    
    Focus on Test-Driven Development and constitutional compliance.
    Return a clear description of the approach.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return response;
    } catch (error) {
      logger.error('Error defining task planning approach:', error);
      return `Task Generation Strategy:
- Generate tasks from design documents (contracts, data model, quickstart)
- Each contract ‚Üí contract test task [P]
- Each entity ‚Üí model creation task [P]
- Each user story ‚Üí integration test task
- Implementation tasks to make tests pass

Ordering Strategy:
- TDD order: Tests before implementation
- Dependency order: Models before services before UI
- Mark [P] for parallel execution (independent files)

Estimated Output: 15-25 numbered, ordered tasks following constitutional principles.`;
    }
  }

  private async generateSummary(spec: GeneratedSpec, context: TechnicalContext): Promise<string> {
    const prompt = `
    Generate a concise summary for this implementation plan:
    
    Feature: ${spec.title}
    Primary Requirements: ${spec.functionalRequirements.slice(0, 3).join(', ')}
    Technical Approach: ${context.language} using ${context.primaryDependencies.join(', ')}
    
    Create a 1-2 sentence summary capturing the essence of what will be built and how.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return response.trim();
    } catch (error) {
      logger.error('Error generating summary:', error);
      return `Implement ${spec.title} using ${context.language} with ${context.primaryDependencies.join(', ')} following test-driven development principles.`;
    }
  }

  private assemblePlan(data: any): string {
    const currentDate = new Date().toISOString().split('T')[0];
    
    let plan = this.planTemplate;
    
    // Replace template variables
    plan = plan.replace(/\{\{FEATURE_NAME\}\}/g, data.spec.title);
    plan = plan.replace(/\{\{BRANCH_NAME\}\}/g, `001-${data.spec.title.toLowerCase().replace(/\s+/g, '-')}`);
    plan = plan.replace(/\{\{DATE\}\}/g, currentDate);
    plan = plan.replace(/\{\{SPEC_LINK\}\}/g, `specs/001-${data.spec.title.toLowerCase().replace(/\s+/g, '-')}/spec.md`);
    plan = plan.replace(/\{\{FEATURE_DIR\}\}/g, `001-${data.spec.title.toLowerCase().replace(/\s+/g, '-')}`);
    
    // Replace content sections
    plan = plan.replace('{{SUMMARY}}', data.summary);
    plan = plan.replace('{{LANGUAGE}}', data.technicalContext.language);
    plan = plan.replace('{{DEPENDENCIES}}', data.technicalContext.primaryDependencies.join(', '));
    plan = plan.replace('{{STORAGE}}', data.technicalContext.storage);
    plan = plan.replace('{{TESTING}}', data.technicalContext.testing);
    plan = plan.replace('{{TARGET_PLATFORM}}', data.technicalContext.targetPlatform);
    plan = plan.replace('{{PROJECT_TYPE}}', data.technicalContext.projectType);
    plan = plan.replace('{{PERFORMANCE_GOALS}}', data.technicalContext.performanceGoals.join(', '));
    plan = plan.replace('{{CONSTRAINTS}}', data.technicalContext.constraints.join(', '));
    plan = plan.replace('{{SCALE}}', data.technicalContext.scale);
    
    // Constitution check
    const constitutionSection = this.formatConstitutionCheck(data.constitutionCheck);
    plan = plan.replace('{{CONSTITUTION_CHECK}}', constitutionSection);
    
    // Project structure
    const structureSection = this.formatProjectStructure(data.projectStructure);
    plan = plan.replace('{{PROJECT_STRUCTURE}}', structureSection);
    
    // Research phase
    const researchSection = this.formatResearchPhase(data.researchTasks);
    plan = plan.replace('{{RESEARCH_PHASE}}', researchSection);
    
    // Design phase
    const designSection = this.formatDesignPhase(data.designPhase);
    plan = plan.replace('{{DESIGN_PHASE}}', designSection);
    
    // Task planning
    plan = plan.replace('{{TASK_PLANNING}}', data.taskPlanningApproach);
    
    // Progress tracking
    const progressSection = this.formatProgressTracking();
    plan = plan.replace('{{PROGRESS_TRACKING}}', progressSection);
    
    return plan;
  }

  private formatConstitutionCheck(check: ConstitutionCheck): string {
    let section = '**Simplicity**:\n';
    section += `- Projects: ${check.simplicity.projectCount} (max 3)\n`;
    section += `- Using framework directly: ${check.simplicity.usingFrameworkDirectly ? 'Yes' : 'No'}\n`;
    section += `- Single data model: ${check.simplicity.singleDataModel ? 'Yes' : 'No'}\n`;
    section += `- Avoiding patterns: ${check.simplicity.avoidingPatterns ? 'Yes' : 'No'}\n\n`;
    
    section += '**Architecture**:\n';
    section += `- Every feature as library: ${check.architecture.everyFeatureAsLibrary ? 'Yes' : 'No'}\n`;
    section += `- Libraries: ${check.architecture.libraries.map(l => `${l.name} (${l.purpose})`).join(', ')}\n`;
    section += `- CLI per library: ${check.architecture.cliPerLibrary.join(', ')}\n`;
    section += `- Library docs planned: ${check.architecture.libraryDocsPlanned ? 'Yes' : 'No'}\n\n`;
    
    section += '**Testing (NON-NEGOTIABLE)**:\n';
    section += `- RED-GREEN-Refactor enforced: ${check.testing.redGreenRefactorEnforced ? 'Yes' : 'No'}\n`;
    section += `- Tests before implementation: ${check.testing.gitCommitsShowTestsFirst ? 'Yes' : 'No'}\n`;
    section += `- Order followed: ${check.testing.orderFollowed ? 'Yes' : 'No'}\n`;
    section += `- Real dependencies: ${check.testing.realDependenciesUsed ? 'Yes' : 'No'}\n`;
    section += `- Integration tests: ${check.testing.integrationTestsPlanned ? 'Yes' : 'No'}\n\n`;
    
    if (check.violations.length > 0) {
      section += '**Violations**:\n';
      section += check.violations.map(v => `- ${v}`).join('\n');
    }
    
    return section;
  }

  private formatProjectStructure(structure: ProjectStructure): string {
    let section = `**Structure Decision**: ${structure.description}\n\n`;
    section += '```\n';
    structure.directories.forEach(dir => {
      section += `${dir}\n`;
    });
    section += '```';
    return section;
  }

  private formatResearchPhase(tasks: ResearchTask[]): string {
    let section = '**Research Tasks**:\n\n';
    tasks.forEach(task => {
      section += `**${task.id}**: ${task.description}\n`;
      section += `- Rationale: ${task.rationale}\n`;
      section += `- Alternatives: ${task.alternatives.join(', ')}\n`;
      if (task.decision) {
        section += `- Decision: ${task.decision}\n`;
      }
      section += '\n';
    });
    return section;
  }

  private formatDesignPhase(design: DesignPhase): string {
    let section = '1. **Data Model**: ' + design.dataModel + '\n\n';
    section += '2. **API Contracts**:\n';
    design.apiContracts.forEach(contract => {
      section += `   - ${contract}\n`;
    });
    section += '\n3. **Contract Tests**:\n';
    design.contractTests.forEach(test => {
      section += `   - ${test}\n`;
    });
    section += '\n4. **Test Scenarios**:\n';
    design.testScenarios.forEach(scenario => {
      section += `   - ${scenario}\n`;
    });
    section += '\n5. **Quickstart**: ' + design.quickstart;
    return section;
  }

  private formatProgressTracking(): string {
    return `**Phase Status**:
- [ ] Phase 0: Research complete (/plan command)
- [ ] Phase 1: Design complete (/plan command)
- [ ] Phase 2: Task planning complete (/plan command - describe approach only)
- [ ] Phase 3: Tasks generated (/tasks command)
- [ ] Phase 4: Implementation complete
- [ ] Phase 5: Validation passed

**Gate Status**:
- [ ] Initial Constitution Check: PASS
- [ ] Post-Design Constitution Check: PASS
- [ ] All NEEDS CLARIFICATION resolved
- [ ] Complexity deviations documented`;
  }

  public async executeResearch(tasks: ResearchTask[]): Promise<ResearchTask[]> {
    logger.info('üîç Executing research tasks...');
    
    const completedTasks = await Promise.all(
      tasks.map(async (task) => {
        const prompt = `
        Research this topic:
        
        Task: ${task.description}
        Rationale: ${task.rationale}
        Alternatives to consider: ${task.alternatives.join(', ')}
        
        Provide:
        1. Recommended decision
        2. Reasoning for the decision
        3. Trade-offs considered
        4. Implementation considerations
        
        Focus on practical, actionable insights for VS Code extension development.
        `;
        
        try {
          const research = await this.llmManager.generateResponse('openai', prompt);
          return {
            ...task,
            decision: research
          };
        } catch (error) {
          logger.error(`Error researching task ${task.id}:`, error);
          return {
            ...task,
            decision: 'Research pending - manual investigation required'
          };
        }
      })
    );
    
    return completedTasks;
  }
}

import { LLMManager } from '../llm/llmManager';
import { VectorDB } from '../db/vectorDB';
import { logger } from '../utils/logger';
import * as fs from 'fs';
import * as path from 'path';

export interface SpecificationRequest {
  userIdea: string;
  projectContext?: string;
  existingRequirements?: string[];
  constraints?: string[];
}

export interface GeneratedSpec {
  title: string;
  content: string;
  clarificationNeeded: string[];
  functionalRequirements: string[];
  userScenarios: string[];
  keyEntities: string[];
  constitutionCompliance: ConstitutionCheck;
}

export interface ConstitutionCheck {
  passed: boolean;
  violations: string[];
  warnings: string[];
  complexityScore: number;
}

export class SpecGenerator {
  private llmManager: LLMManager;
  private vectorDB: VectorDB;
  private specTemplate: string = '';

  constructor(llmManager: LLMManager, vectorDB: VectorDB) {
    this.llmManager = llmManager;
    this.vectorDB = vectorDB;
    this.loadSpecTemplate();
  }

  private loadSpecTemplate(): void {
    const templatePath = path.join(__dirname, '../../templates/spec-template.md');
    try {
      this.specTemplate = fs.readFileSync(templatePath, 'utf8');
    } catch (error) {
      console.error('Failed to load spec template:', error);
      this.specTemplate = this.getDefaultSpecTemplate();
    }
  }

  private getDefaultSpecTemplate(): string {
    return `# Feature Specification: {{FEATURE_NAME}}

**Feature Branch**: \`{{BRANCH_NAME}}\`  
**Created**: {{DATE}}  
**Status**: Draft  
**Input**: User description: "{{USER_INPUT}}"

## User Scenarios & Testing

### Primary User Story
{{PRIMARY_USER_STORY}}

### Acceptance Scenarios
{{ACCEPTANCE_SCENARIOS}}

### Edge Cases
{{EDGE_CASES}}

## Requirements

### Functional Requirements
{{FUNCTIONAL_REQUIREMENTS}}

### Key Entities
{{KEY_ENTITIES}}

## Review & Acceptance Checklist

### Content Quality
- [ ] No implementation details (languages, frameworks, APIs)
- [ ] Focused on user value and business needs
- [ ] Written for non-technical stakeholders
- [ ] All mandatory sections completed

### Requirement Completeness
- [ ] No [NEEDS CLARIFICATION] markers remain
- [ ] Requirements are testable and unambiguous  
- [ ] Success criteria are measurable
- [ ] Scope is clearly bounded
- [ ] Dependencies and assumptions identified

## Clarifications Needed
{{CLARIFICATIONS_NEEDED}}
`;
  }

  public async generateSpecification(request: SpecificationRequest): Promise<GeneratedSpec> {
    logger.info('Generating specification from user idea...');

    // Step 1: Parse and understand the user idea
    const parsedIdea = await this.parseUserIdea(request.userIdea, request.projectContext);

    // Step 2: Generate specification sections using multi-LLM collaboration
    const specSections = await this.generateSpecSections(parsedIdea, request);

    // Step 3: Check constitution compliance
    const constitutionCheck = await this.checkConstitutionCompliance(specSections);

    // Step 4: Identify clarification needs
    const clarifications = await this.identifyClarifications(specSections);

    // Step 5: Assemble the final specification
    const finalSpec = this.assembleSpecification(request, specSections, clarifications);

    return {
      title: specSections.featureName,
      content: finalSpec,
      clarificationNeeded: clarifications,
      functionalRequirements: specSections.functionalRequirements,
      userScenarios: specSections.userScenarios,
      keyEntities: specSections.keyEntities,
      constitutionCompliance: constitutionCheck
    };
  }

  private async parseUserIdea(userIdea: string, context?: string): Promise<any> {
    const prompt = `
    Parse this user idea into structured components:
    
    User Idea: "${userIdea}"
    ${context ? `Project Context: ${context}` : ''}
    
    Extract:
    1. Core functionality (what the user wants)
    2. Key actors/users involved
    3. Main actions/workflows
    4. Data entities mentioned
    5. Constraints or requirements
    6. Success criteria (if mentioned)
    
    Return as structured JSON with clear categories.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error parsing user idea:', error);
      // Fallback to simple parsing
      return {
        coreFunctionality: userIdea,
        actors: ['User'],
        actions: ['Use the system'],
        entities: [],
        constraints: [],
        successCriteria: []
      };
    }
  }

  private async generateSpecSections(parsedIdea: any, request: SpecificationRequest): Promise<any> {
    // Use multi-LLM collaboration for better spec generation
    const llmProviders = ['openai', 'anthropic', 'openrouter'];
    const sections: any = {};

    // Generate feature name
    sections.featureName = await this.generateFeatureName(parsedIdea);
    sections.branchName = this.generateBranchName(sections.featureName);

    // Generate user scenarios using different LLMs for diversity
    sections.userScenarios = await this.generateUserScenarios(parsedIdea, llmProviders[0]);
    sections.acceptanceScenarios = await this.generateAcceptanceScenarios(parsedIdea, llmProviders[1]);
    sections.edgeCases = await this.generateEdgeCases(parsedIdea, llmProviders[2]);

    // Generate functional requirements
    sections.functionalRequirements = await this.generateFunctionalRequirements(parsedIdea, sections.userScenarios);

    // Identify key entities
    sections.keyEntities = await this.identifyKeyEntities(parsedIdea, sections.functionalRequirements);

    return sections;
  }

  private async generateFeatureName(parsedIdea: any): Promise<string> {
    const prompt = `
    Generate a concise, descriptive feature name for this functionality:
    ${JSON.stringify(parsedIdea, null, 2)}
    
    Requirements:
    - 2-5 words maximum
    - Clear and professional
    - Focuses on user value
    - Suitable for branch names
    
    Return only the feature name, no explanation.
    `;

    const response = await this.llmManager.generateResponse('openai', prompt);
    return response.trim().replace(/[^a-zA-Z0-9\s-]/g, '').replace(/\s+/g, '-').toLowerCase();
  }

  private generateBranchName(featureName: string): string {
    const timestamp = new Date().toISOString().split('T')[0].replace(/-/g, '');
    return `001-${featureName}-${timestamp}`;
  }

  private async generateUserScenarios(parsedIdea: any, provider: string): Promise<string[]> {
    const prompt = `
    Create primary user scenarios for this feature:
    ${JSON.stringify(parsedIdea, null, 2)}
    
    Generate 1-3 clear user scenarios in this format:
    "As a [user type], I want to [action] so that [benefit]"
    
    Focus on the main value propositions and user workflows.
    Return as JSON array of strings.
    `;

    try {
      const response = await this.llmManager.generateResponse(provider, prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error generating user scenarios:', error);
      return [`As a user, I want to ${parsedIdea.coreFunctionality} so that I can achieve my goals`];
    }
  }

  private async generateAcceptanceScenarios(parsedIdea: any, provider: string): Promise<string[]> {
    const prompt = `
    Create acceptance scenarios in Given-When-Then format for:
    ${JSON.stringify(parsedIdea, null, 2)}
    
    Generate 3-5 scenarios covering:
    1. Happy path (main functionality)
    2. Alternative flows
    3. Error conditions
    
    Format: "Given [initial state], When [action], Then [expected outcome]"
    Return as JSON array of strings.
    `;

    try {
      const response = await this.llmManager.generateResponse(provider, prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error generating acceptance scenarios:', error);
      return ['Given initial state, When user performs action, Then system responds appropriately'];
    }
  }

  private async generateEdgeCases(parsedIdea: any, provider: string): Promise<string[]> {
    const prompt = `
    Identify potential edge cases and error scenarios for:
    ${JSON.stringify(parsedIdea, null, 2)}
    
    Consider:
    1. Invalid inputs
    2. System limitations
    3. Network/connectivity issues
    4. Concurrent usage
    5. Data boundary conditions
    
    Return as JSON array of strings describing each edge case.
    `;

    try {
      const response = await this.llmManager.generateResponse(provider, prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error generating edge cases:', error);
      return ['What happens when invalid input is provided?', 'How does system handle high load?'];
    }
  }

  private async generateFunctionalRequirements(parsedIdea: any, userScenarios: string[]): Promise<string[]> {
    const prompt = `
    Generate functional requirements from this analysis:
    
    Parsed Idea: ${JSON.stringify(parsedIdea, null, 2)}
    User Scenarios: ${JSON.stringify(userScenarios, null, 2)}
    
    Create 5-10 functional requirements in format:
    "FR-001: System MUST [specific capability]"
    "FR-002: Users MUST be able to [specific action]"
    
    Requirements should be:
    - Testable and measurable
    - Focused on WHAT, not HOW
    - Cover all major functionality
    - Include validation and error handling
    
    Return as JSON array of strings.
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error generating functional requirements:', error);
      return ['FR-001: System MUST provide core functionality', 'FR-002: Users MUST be able to interact with the system'];
    }
  }

  private async identifyKeyEntities(parsedIdea: any, requirements: string[]): Promise<string[]> {
    const prompt = `
    Identify key data entities from this analysis:
    
    Parsed Idea: ${JSON.stringify(parsedIdea, null, 2)}
    Functional Requirements: ${JSON.stringify(requirements, null, 2)}
    
    For each entity, provide:
    - Entity name
    - Brief description of what it represents
    - Key attributes (conceptual, not technical)
    - Relationships to other entities
    
    Focus on business/domain entities, not technical implementation.
    Return as JSON array of objects with name, description, attributes, relationships.
    `;

    try {
      const response = await this.llmManager.generateResponse('anthropic', prompt);
      const entities = JSON.parse(response);
      return entities.map((entity: any) => `${entity.name}: ${entity.description}`);
    } catch (error) {
      console.error('Error identifying key entities:', error);
      return ['User: Person who interacts with the system'];
    }
  }

  private async checkConstitutionCompliance(specSections: any): Promise<ConstitutionCheck> {
    const prompt = `
    Check this specification against AstraForge development constitution:
    
    Specification: ${JSON.stringify(specSections, null, 2)}
    
    Evaluate:
    1. Simplicity (avoid over-engineering)
    2. Clear scope and boundaries
    3. Testable requirements
    4. User-focused (not tech-focused)
    5. Appropriate complexity level
    
    Return JSON with:
    {
      "passed": boolean,
      "violations": ["specific issues"],
      "warnings": ["potential concerns"],
      "complexityScore": 1-10 (1=simple, 10=very complex)
    }
    `;

    try {
      const response = await this.llmManager.generateResponse('openai', prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error checking constitution compliance:', error);
      return {
        passed: true,
        violations: [],
        warnings: [],
        complexityScore: 5
      };
    }
  }

  private async identifyClarifications(specSections: any): Promise<string[]> {
    const prompt = `
    Identify areas that need clarification in this specification:
    
    ${JSON.stringify(specSections, null, 2)}
    
    Look for:
    1. Ambiguous requirements
    2. Missing user types/roles
    3. Undefined business rules
    4. Unclear success criteria
    5. Integration assumptions
    6. Performance/scale expectations
    
    Return JSON array of specific questions that need answers.
    Format: "NEEDS CLARIFICATION: [specific question]"
    `;

    try {
      const response = await this.llmManager.generateResponse('anthropic', prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error identifying clarifications:', error);
      return [];
    }
  }

  private assembleSpecification(request: SpecificationRequest, sections: any, clarifications: string[]): string {
    const currentDate = new Date().toISOString().split('T')[0];
    
    let spec = this.specTemplate;
    
    // Replace template variables
    spec = spec.replace('{{FEATURE_NAME}}', sections.featureName || 'New Feature');
    spec = spec.replace('{{BRANCH_NAME}}', sections.branchName || '001-new-feature');
    spec = spec.replace('{{DATE}}', currentDate);
    spec = spec.replace('{{USER_INPUT}}', request.userIdea || 'User provided idea');
    
    // Replace content sections
    spec = spec.replace('{{PRIMARY_USER_STORY}}', sections.userScenarios?.join('\n\n') || 'User story to be defined');
    spec = spec.replace('{{ACCEPTANCE_SCENARIOS}}', sections.acceptanceScenarios?.map((s: string, i: number) => `${i + 1}. ${s}`).join('\n') || 'Scenarios to be defined');
    spec = spec.replace('{{EDGE_CASES}}', sections.edgeCases?.map((e: string) => `- ${e}`).join('\n') || 'Edge cases to be identified');
    spec = spec.replace('{{FUNCTIONAL_REQUIREMENTS}}', sections.functionalRequirements?.map((r: string) => `- **${r}**`).join('\n') || 'Requirements to be defined');
    spec = spec.replace('{{KEY_ENTITIES}}', sections.keyEntities?.map((e: string) => `- **${e}**`).join('\n') || 'Entities to be identified');
    spec = spec.replace('{{CLARIFICATIONS_NEEDED}}', clarifications.map(c => `- ${c}`).join('\n') || 'No clarifications needed');
    
    return spec;
  }

  public async refineSpecification(existingSpec: string, refinements: string[]): Promise<GeneratedSpec> {
    logger.info('Refining specification...');
    const prompt = `
    Refine this existing specification based on user feedback:
    
    Existing Spec:
    ${existingSpec}
    
    Refinements Requested:
    ${refinements.join('\n')}
    
    Update the specification to address the refinements while maintaining structure and quality.
    Return the complete refined specification.
    `;

    const refinedContent = await this.llmManager.generateResponse('openai', prompt);
    
    // Re-analyze the refined specification
    const analysis = await this.analyzeSpecification(refinedContent);
    
    return {
      title: analysis.title,
      content: refinedContent,
      clarificationNeeded: analysis.clarificationNeeded,
      functionalRequirements: analysis.functionalRequirements,
      userScenarios: analysis.userScenarios,
      keyEntities: analysis.keyEntities,
      constitutionCompliance: analysis.constitutionCompliance
    };
  }

  private async analyzeSpecification(specContent: string): Promise<any> {
    // Extract information from the specification content
    const prompt = `
    Analyze this specification and extract structured information:
    
    ${specContent}
    
    Return JSON with:
    {
      "title": "feature name",
      "clarificationNeeded": ["list of clarifications"],
      "functionalRequirements": ["list of requirements"],
      "userScenarios": ["list of scenarios"],
      "keyEntities": ["list of entities"],
      "constitutionCompliance": {
        "passed": boolean,
        "violations": [],
        "warnings": [],
        "complexityScore": number
      }
    }
    `;

    try {
      const response = await this.llmManager.generateResponse('anthropic', prompt);
      return JSON.parse(response);
    } catch (error) {
      console.error('Error analyzing specification:', error);
      return {
        title: 'Analyzed Feature',
        clarificationNeeded: [],
        functionalRequirements: [],
        userScenarios: [],
        keyEntities: [],
        constitutionCompliance: { passed: true, violations: [], warnings: [], complexityScore: 5 }
      };
    }
  }
}

