import * as vscode from 'vscode';
import { ApiTesterCore, TestResult, BatchTestResult, VectorTestResult } from './apiTesterCore';

export class ApiTesterProvider implements vscode.WebviewViewProvider {
  public static readonly viewType = 'astraforge.apiTester';
  private _view?: vscode.Webview;
  private _tester: ApiTesterCore;

  constructor(private readonly _extensionUri: vscode.Uri) {
    this._tester = new ApiTesterCore();
  }

  public resolveWebviewView(
    webviewView: vscode.WebviewView,
    _context: vscode.WebviewViewResolveContext,
    _token: vscode.CancellationToken
  ) {
    this._view = webviewView.webview;

    webviewView.webview.options = {
      enableScripts: true,
      localResourceRoots: [this._extensionUri],
    };

    webviewView.webview.html = this._getHtmlForWebview(webviewView.webview);

    // Handle messages from the webview
    webviewView.webview.onDidReceiveMessage(async data => {
      try {
        switch (data.type) {
          case 'initialize':
            await this._tester.initialize();
            this._sendMessage('initialized', { success: true });
            break;

          case 'testLLM':
            await this._handleLLMTest(data);
            break;

          case 'testBatch':
            await this._handleBatchTest(data);
            break;

          case 'testVector':
            await this._handleVectorTest(data);
            break;

          case 'testWorkflow':
            await this._handleWorkflowTest(data);
            break;

          case 'validateKey':
            const isValid = this._tester.validateApiKey(data.provider, data.key);
            this._sendMessage('keyValidated', { isValid, provider: data.provider });
            break;

          case 'getProviders':
            const providers = this._tester.getSupportedProviders();
            this._sendMessage('providersList', { providers });
            break;

          case 'getModels':
            const models = this._tester.getSupportedModels(data.provider);
            this._sendMessage('modelsList', { models, provider: data.provider });
            break;

          default:
            console.warn('Unknown message type:', data.type);
        }
      } catch (error: any) {
        this._sendMessage('error', {
          message: error.message,
          type: data.type,
        });
      }
    });
  }

  private async _handleLLMTest(data: any) {
    const result = await this._tester.testLLM(data.provider, data.apiKey, data.model, data.prompt);

    this._sendMessage('llmTestResult', {
      result,
      requestId: data.requestId,
    });
  }

  private async _handleBatchTest(data: any) {
    const result = await this._tester.testBatchLLM(
      data.provider,
      data.apiKey,
      data.model,
      data.prompts
    );

    this._sendMessage('batchTestResult', {
      result,
      requestId: data.requestId,
    });
  }

  private async _handleVectorTest(data: any) {
    const result = await this._tester.testVectorQuery(data.query, data.topK || 5);

    this._sendMessage('vectorTestResult', {
      result,
      requestId: data.requestId,
    });
  }

  private async _handleWorkflowTest(data: any) {
    const results = await this._tester.testWorkflowSimulation(
      data.idea,
      data.provider,
      data.apiKey,
      data.model
    );

    this._sendMessage('workflowTestResult', {
      results,
      requestId: data.requestId,
    });
  }

  private _sendMessage(type: string, data: any) {
    if (this._view) {
      this._view.postMessage({ type, data });
    }
  }

  private _getHtmlForWebview(webview: vscode.Webview) {
    const scriptUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'apiTester.js')
    );
    const styleUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'styles.css')
    );

    return `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>AstraForge API Tester</title>
  <link href="${styleUri}" rel="stylesheet">
  <style>
    .api-tester {
      padding: 16px;
      font-family: var(--vscode-font-family);
      color: var(--vscode-foreground);
    }
    .form-group {
      margin-bottom: 12px;
    }
    .form-group label {
      display: block;
      margin-bottom: 4px;
      font-weight: 500;
    }
    .form-group input, .form-group select, .form-group textarea {
      width: 100%;
      padding: 8px;
      border: 1px solid var(--vscode-input-border);
      background: var(--vscode-input-background);
      color: var(--vscode-input-foreground);
      border-radius: 4px;
    }
    .form-group textarea {
      min-height: 80px;
      resize: vertical;
    }
    .button-group {
      display: flex;
      gap: 8px;
      margin: 16px 0;
    }
    .btn {
      padding: 8px 16px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 12px;
    }
    .btn-primary {
      background: var(--vscode-button-background);
      color: var(--vscode-button-foreground);
    }
    .btn-secondary {
      background: var(--vscode-button-secondaryBackground);
      color: var(--vscode-button-secondaryForeground);
    }
    .results {
      margin-top: 16px;
      padding: 12px;
      background: var(--vscode-textBlockQuote-background);
      border-radius: 4px;
      max-height: 300px;
      overflow-y: auto;
    }
    .result-item {
      margin-bottom: 8px;
      padding: 8px;
      background: var(--vscode-editor-background);
      border-radius: 4px;
    }
    .status-success {
      color: var(--vscode-testing-iconPassed);
    }
    .status-error {
      color: var(--vscode-testing-iconFailed);
    }
    .loading {
      opacity: 0.6;
      pointer-events: none;
    }
  </style>
</head>
<body>
  <div class="api-tester">
    <h2>AstraForge API Tester</h2>

    <div class="form-group">
      <label for="provider">API Provider:</label>
      <select id="provider">
        <option value="OpenAI">OpenAI</option>
        <option value="Anthropic">Anthropic</option>
        <option value="xAI">xAI</option>
        <option value="OpenRouter">OpenRouter</option>
      </select>
    </div>

    <div class="form-group">
      <label for="apiKey">API Key:</label>
      <input type="password" id="apiKey" placeholder="Enter your API key">
      <button class="btn btn-secondary" onclick="validateKey()">Validate Key</button>
    </div>

    <div class="form-group">
      <label for="model">Model:</label>
      <select id="model">
        <option value="gpt-4">gpt-4</option>
        <option value="gpt-4-turbo">gpt-4-turbo</option>
        <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
      </select>
    </div>

    <div class="form-group">
      <label for="prompt">Test Prompt:</label>
      <textarea id="prompt" placeholder="Enter your test prompt here..."></textarea>
    </div>

    <div class="button-group">
      <button class="btn btn-primary" onclick="testLLM()">Test LLM</button>
      <button class="btn btn-secondary" onclick="testVector()">Test Vector</button>
      <button class="btn btn-secondary" onclick="testWorkflow()">Test Workflow</button>
      <button class="btn btn-secondary" onclick="clearResults()">Clear</button>
    </div>

    <div id="results" class="results" style="display: none;">
      <h3>Results:</h3>
      <div id="resultsContent"></div>
    </div>
  </div>

  <script src="${scriptUri}"></script>
</body>
</html>`;
  }
}

/**
 * Environment variable loader utility for AstraForge API Tester
 */
import * as fs from 'fs';
import * as path from 'path';
import { logger } from './logger';

export interface EnvConfig {
  // API Keys
  OPENROUTER_API_KEY?: string;
  OPENAI_API_KEY?: string;
  ANTHROPIC_API_KEY?: string;
  XAI_API_KEY?: string;
  HUGGINGFACE_API_TOKEN?: string;
  
  // Model Configuration
  OPENROUTER_MODELS_TO_USE?: string;
  
  // AstraForge Configuration
  DEFAULT_LLM_PROVIDER?: string;
  AUTO_COMMIT_ENABLED?: string;
  VECTOR_DB_PATH?: string;
  DEBUG_MODE?: string;
  
  // Spec-Driven Development
  ENFORCE_CONSTITUTION?: string;
  MIN_TEST_COVERAGE?: string;
  ENABLE_PARALLEL_TASKS?: string;
  
  // Security Settings
  USE_SECURE_STORAGE?: string;
  API_TIMEOUT?: string;
  MAX_TOKENS_PER_REQUEST?: string;
  
  // Usage and Monitoring
  TRACK_API_USAGE?: string;
  DAILY_BUDGET_LIMIT?: string;
  ENABLE_PERFORMANCE_MONITORING?: string;
  
  // Collaboration Server
  ENABLE_COLLABORATION_SERVER?: string;
  COLLABORATION_SERVER_PORT?: string;
  
  // Development Preferences
  DEFAULT_LANGUAGE?: string;
  DEFAULT_TESTING_FRAMEWORK?: string;
  DEFAULT_PROJECT_TYPE?: string;
  AUTO_FORMAT_CODE?: string;
  AUTO_ORGANIZE_IMPORTS?: string;
  
  // UI Preferences
  SHOW_INLINE_SUGGESTIONS?: string;
  ENABLE_SYNTAX_HIGHLIGHTING?: string;
  CODE_THEME?: string;
  SHOW_PROGRESS_NOTIFICATIONS?: string;
}

export class EnvLoader {
  private config: EnvConfig = {};

  constructor(private projectRoot: string = process.cwd()) {
    this.loadEnvFile();
  }

  private loadEnvFile(): void {
    const envPath = path.join(this.projectRoot, '.env');
    
    if (!fs.existsSync(envPath)) {
      logger.warn('No .env file found. Using system environment variables only.');
      return;
    }

    try {
      const envContent = fs.readFileSync(envPath, 'utf8');
      const lines = envContent.split('\n');

      for (const line of lines) {
        const trimmedLine = line.trim();
        
        // Skip empty lines and comments
        if (!trimmedLine || trimmedLine.startsWith('#')) {
          continue;
        }

        const [key, ...valueParts] = trimmedLine.split('=');
        if (key && valueParts.length > 0) {
          const value = valueParts.join('=').trim();
          this.config[key.trim() as keyof EnvConfig] = value;
        }
      }
    } catch (error) {
      logger.error('Error reading .env file:', error);
    }
  }

  /**
   * Get environment variable with fallback to system env
   */
  get(key: keyof EnvConfig): string | undefined {
    return this.config[key] || process.env[key];
  }

  /**
   * Get OpenRouter API key
   */
  getOpenRouterApiKey(): string | undefined {
    return this.get('OPENROUTER_API_KEY');
  }

  /**
   * Get OpenRouter models as array
   */
  getOpenRouterModels(): string[] {
    const modelsString = this.get('OPENROUTER_MODELS_TO_USE');
    if (!modelsString) {
      return [];
    }

    return modelsString
      .split(',')
      .map(model => model.trim())
      .filter(model => model.length > 0);
  }

  /**
   * Get configuration for 3-LLM panel based on your env
   */
  getLLMPanelConfig(): Array<{
    provider: 'OpenAI' | 'Anthropic' | 'xAI' | 'OpenRouter';
    apiKey: string;
    model: string;
    role: string;
  }> {
    const apiKey = this.getOpenRouterApiKey();
    const models = this.getOpenRouterModels();

    if (!apiKey) {
      throw new Error('Missing API key in .env file. Please ensure OPENROUTER_API_KEY is set.');
    }
    
    if (models.length < 3) {
      throw new Error(`Insufficient models in .env file. Found ${models.length}, need 3. Please check OPENROUTER_MODELS_TO_USE format.`);
    }

    return [
      {
        provider: 'OpenRouter' as const,
        apiKey,
        model: models[0], // x-ai/grok-4
        role: 'concept'
      },
      {
        provider: 'OpenRouter' as const, 
        apiKey,
        model: models[1], // google/gemini-2.5-pro
        role: 'development'
      },
      {
        provider: 'OpenRouter' as const,
        apiKey,
        model: models[2], // anthropic/claude-sonnet-4
        role: 'coding'
      }
    ];
  }

  /**
   * Validate that all required environment variables are present
   */
  validate(): { valid: boolean; missing: string[] } {
    const required = ['OPENROUTER_API_KEY', 'OPENROUTER_MODELS_TO_USE'];
    const missing: string[] = [];

    for (const key of required) {
      if (!this.get(key as keyof EnvConfig)) {
        missing.push(key);
      }
    }

    return {
      valid: missing.length === 0,
      missing
    };
  }

  /**
   * Get all loaded configuration
   */
  getConfig(): EnvConfig {
    return { ...this.config };
  }

  /**
   * Get boolean value with default
   */
  getBoolean(key: keyof EnvConfig, defaultValue: boolean = false): boolean {
    const value = this.get(key);
    if (!value) return defaultValue;
    return value.toLowerCase() === 'true';
  }

  /**
   * Get number value with default
   */
  getNumber(key: keyof EnvConfig, defaultValue: number = 0): number {
    const value = this.get(key);
    if (!value) return defaultValue;
    const parsed = parseInt(value, 10);
    return isNaN(parsed) ? defaultValue : parsed;
  }

  /**
   * Get API key for any provider
   */
  getApiKey(provider: string): string | undefined {
    switch (provider.toLowerCase()) {
      case 'openrouter':
        return this.get('OPENROUTER_API_KEY');
      case 'openai':
        return this.get('OPENAI_API_KEY');
      case 'anthropic':
        return this.get('ANTHROPIC_API_KEY');
      case 'xai':
        return this.get('XAI_API_KEY');
      default:
        return undefined;
    }
  }

  /**
   * Get default LLM provider
   */
  getDefaultProvider(): string {
    return this.get('DEFAULT_LLM_PROVIDER') || 'openrouter';
  }

  /**
   * Check if debug mode is enabled
   */
  isDebugMode(): boolean {
    return this.getBoolean('DEBUG_MODE', false);
  }

  /**
   * Check if auto-commit is enabled
   */
  isAutoCommitEnabled(): boolean {
    return this.getBoolean('AUTO_COMMIT_ENABLED', true);
  }

  /**
   * Check if constitution enforcement is enabled
   */
  isConstitutionEnforced(): boolean {
    return this.getBoolean('ENFORCE_CONSTITUTION', true);
  }

  /**
   * Get minimum test coverage requirement
   */
  getMinTestCoverage(): number {
    return this.getNumber('MIN_TEST_COVERAGE', 85);
  }

  /**
   * Get API timeout in milliseconds
   */
  getApiTimeout(): number {
    return this.getNumber('API_TIMEOUT', 30000);
  }

  /**
   * Get max tokens per request
   */
  getMaxTokensPerRequest(): number {
    return this.getNumber('MAX_TOKENS_PER_REQUEST', 4000);
  }

  /**
   * Get daily budget limit
   */
  getDailyBudgetLimit(): number {
    const value = this.get('DAILY_BUDGET_LIMIT');
    if (!value) return 0;
    const parsed = parseFloat(value);
    return isNaN(parsed) ? 0 : parsed;
  }

  /**
   * Get collaboration server port
   */
  getCollaborationServerPort(): number {
    return this.getNumber('COLLABORATION_SERVER_PORT', 3001);
  }

  /**
   * Check if usage tracking is enabled
   */
  isUsageTrackingEnabled(): boolean {
    return this.getBoolean('TRACK_API_USAGE', true);
  }

  /**
   * Get vector database path
   */
  getVectorDbPath(): string {
    return this.get('VECTOR_DB_PATH') || '.astraforge/vectordb';
  }

  /**
   * Get comprehensive configuration summary for debugging
   */
  getConfigSummary(): {
    providers: string[];
    hasApiKeys: boolean;
    debugMode: boolean;
    autoCommit: boolean;
    constitutionEnforced: boolean;
    testCoverage: number;
  } {
    const providers = [];
    if (this.get('OPENROUTER_API_KEY')) providers.push('OpenRouter');
    if (this.get('OPENAI_API_KEY')) providers.push('OpenAI');
    if (this.get('ANTHROPIC_API_KEY')) providers.push('Anthropic');
    if (this.get('XAI_API_KEY')) providers.push('xAI');

    return {
      providers,
      hasApiKeys: providers.length > 0,
      debugMode: this.isDebugMode(),
      autoCommit: this.isAutoCommitEnabled(),
      constitutionEnforced: this.isConstitutionEnforced(),
      testCoverage: this.getMinTestCoverage()
    };
  }
}

// Export singleton instance
export const envLoader = new EnvLoader();

/**
 * Input Validation and Sanitization Utilities
 * Ensures all user inputs are properly validated before sending to LLMs
 */

/**
 * Validation result interface
 */
export interface ValidationResult {
  isValid: boolean;
  errors: string[];
  sanitized?: string;
}

/**
 * Validation options
 */
export interface ValidationOptions {
  maxLength?: number;
  minLength?: number;
  allowHtml?: boolean;
  allowScripts?: boolean;
  customPatterns?: RegExp[];
}

/**
 * Default validation options for LLM prompts
 */
const DEFAULT_PROMPT_OPTIONS: ValidationOptions = {
  maxLength: 10000,
  minLength: 1,
  allowHtml: false,
  allowScripts: false,
  customPatterns: [],
};

/**
 * Sanitize and validate user input for LLM consumption
 *
 * @param input - The user input to validate
 * @param options - Validation options
 * @returns Validation result with sanitized input
 */
export function validateLLMInput(
  input: string,
  options: ValidationOptions = DEFAULT_PROMPT_OPTIONS
): ValidationResult {
  const errors: string[] = [];
  let sanitized = input;

  // Basic null/undefined check
  if (!input || typeof input !== 'string') {
    return {
      isValid: false,
      errors: ['Input must be a non-empty string'],
    };
  }

  // Length validation
  if (options.maxLength && input.length > options.maxLength) {
    errors.push(`Input exceeds maximum length of ${options.maxLength} characters`);
  }

  if (options.minLength && input.length < options.minLength) {
    errors.push(`Input must be at least ${options.minLength} characters long`);
  }

  // Remove control characters and potentially dangerous content
  sanitized = sanitized.replace(/[\x00-\x1F\x7F]/g, '');

  // HTML/Script sanitization
  if (!options.allowHtml) {
    sanitized = sanitized.replace(/<[^>]*>/g, '');
  }

  if (!options.allowScripts) {
    sanitized = sanitized.replace(/<script[^>]*>.*?<\/script>/gi, '');
    sanitized = sanitized.replace(/javascript:/gi, '');
    sanitized = sanitized.replace(/on\w+\s*=/gi, '');
  }

  // Check for suspicious patterns
  const suspiciousPatterns = [
    /system\s*:/gi,
    /(?:ignore|disregard|forget).*(?:instructions|prompt|rules)/gi,
    /(?:act|pretend|roleplay)\s+as/gi,
    /(?:you\s+are|you're)\s+(?:now|a)/gi,
  ];

  for (const pattern of suspiciousPatterns) {
    if (pattern.test(sanitized)) {
      errors.push('Input contains potentially suspicious content');
      break;
    }
  }

  // Custom pattern validation
  if (options.customPatterns) {
    for (const pattern of options.customPatterns) {
      if (pattern.test(sanitized)) {
        errors.push('Input violates custom validation pattern');
        break;
      }
    }
  }

  // Check for potential injection attempts
  const injectionPatterns = [
    /\${.*}/g, // Template literals
    /<%.*%>/g, // Template tags
    /{{.*}}/g, // Handlebars/Mustache
    /\[\[.*\]\]/g, // Wiki/Markdown links that could be exploited
  ];

  for (const pattern of injectionPatterns) {
    if (pattern.test(sanitized)) {
      errors.push('Input contains potentially unsafe template syntax');
      break;
    }
  }

  return {
    isValid: errors.length === 0,
    errors,
    sanitized: errors.length === 0 ? sanitized.trim() : undefined,
  };
}

/**
 * Validate API key format
 *
 * @param apiKey - The API key to validate
 * @param provider - The provider name for specific validation rules
 * @returns Validation result
 */
export function validateApiKey(apiKey: string, provider: string): ValidationResult {
  const errors: string[] = [];

  if (!apiKey || typeof apiKey !== 'string') {
    return {
      isValid: false,
      errors: ['API key must be a non-empty string'],
    };
  }

  // Remove whitespace
  const cleanKey = apiKey.trim();

  // Basic length validation
  if (cleanKey.length < 10) {
    errors.push('API key appears to be too short');
  }

  // Provider-specific validation
  switch (provider.toLowerCase()) {
    case 'openai':
      if (!cleanKey.startsWith('sk-')) {
        errors.push('OpenAI API keys should start with "sk-"');
      }
      if (cleanKey.length < 51) {
        errors.push('OpenAI API key appears to be invalid length');
      }
      break;

    case 'anthropic':
      if (!cleanKey.startsWith('sk-ant-')) {
        errors.push('Anthropic API keys should start with "sk-ant-"');
      }
      break;

    case 'xai':
      if (!cleanKey.startsWith('xai-')) {
        errors.push('xAI API keys should start with "xai-"');
      }
      break;

    case 'openrouter':
      if (!cleanKey.startsWith('sk-or-')) {
        errors.push('OpenRouter API keys should start with "sk-or-"');
      }
      break;
  }

  // Check for suspicious characters
  if (!/^[a-zA-Z0-9\-_]+$/.test(cleanKey)) {
    errors.push('API key contains invalid characters');
  }

  return {
    isValid: errors.length === 0,
    errors,
    sanitized: errors.length === 0 ? cleanKey : undefined,
  };
}

/**
 * Validate project idea input
 *
 * @param idea - The project idea to validate
 * @returns Validation result
 */
export function validateProjectIdea(idea: string): ValidationResult {
  return validateLLMInput(idea, {
    maxLength: 5000,
    minLength: 10,
    allowHtml: false,
    allowScripts: false,
  });
}

/**
 * Validate file content before processing
 *
 * @param content - File content to validate
 * @param filename - Optional filename for context
 * @returns Validation result
 */
export function validateFileContent(content: string, filename?: string): ValidationResult {
  const errors: string[] = [];

  if (!content || typeof content !== 'string') {
    return {
      isValid: false,
      errors: ['File content must be a string'],
    };
  }

  // Check file size (10MB limit)
  if (content.length > 10 * 1024 * 1024) {
    errors.push('File content exceeds 10MB limit');
  }

  // Check for binary content
  if (/[\x00-\x08\x0E-\x1F\x7F]/.test(content)) {
    errors.push('File appears to contain binary data');
  }

  // Filename validation
  if (filename) {
    const dangerousExtensions = ['.exe', '.bat', '.cmd', '.scr', '.com'];
    const ext = filename.toLowerCase().substring(filename.lastIndexOf('.'));

    if (dangerousExtensions.includes(ext)) {
      errors.push('File type not allowed');
    }
  }

  return {
    isValid: errors.length === 0,
    errors,
    sanitized: errors.length === 0 ? content : undefined,
  };
}

/**
 * AstraForge Workflow Manager
 *
 * Manages the complete software development workflow from idea to deployment.
 * Integrates LLM collaboration, vector-based context retrieval, Git version control,
 * and reinforcement learning for workflow optimization.
 *
 * @author AstraForge Team
 * @version 1.0.0
 */

import * as vscode from 'vscode';
import { LLMManager } from '../llm/llmManager';
import { VectorDB } from '../db/vectorDB';
import { GitManager } from '../git/gitManager';
import { AdaptiveWorkflowRL } from '../rl/adaptiveWorkflow';
import { CollaborationServer } from '../server/collaborationServer';
import * as path from 'path';

/**
 * Metrics tracking for workflow performance and user engagement
 */
interface WorkflowMetrics {
  /** Timestamp when workflow started */
  startTime: number;
  /** Timestamp when current phase started */
  phaseStartTime: number;
  /** Number of errors encountered */
  errors: number;
  /** Array of user feedback scores (0-1) */
  userFeedback: number[];
  /** Number of iterations performed */
  iterations: number;
}

/**
 * Main workflow orchestrator that manages the complete development lifecycle
 *
 * Features:
 * - Phase-based development (Planning ‚Üí Prototyping ‚Üí Testing ‚Üí Deployment)
 * - Multi-LLM collaboration and consensus voting
 * - Vector-based context retrieval for consistency
 * - Reinforcement learning for workflow optimization
 * - Real-time collaboration support
 * - Git integration for version control
 * - User oversight and feedback integration
 */
export class WorkflowManager {
  /** Current phase index in the workflow */
  private currentPhase = 0;

  /** Ordered list of workflow phases */
  private phases = ['Planning', 'Prototyping', 'Testing', 'Deployment'];

  /** User's project idea/description */
  private projectIdea: string = '';

  /** Generated project plan */
  private buildPlan: string = '';

  /** Reinforcement learning system for workflow optimization */
  private workflowRL: AdaptiveWorkflowRL;

  /** Optional collaboration server for multi-user workflows */
  private collaborationServer?: CollaborationServer;

  /** Performance and engagement metrics */
  private metrics: WorkflowMetrics;

  /** Unique identifier for this workspace session */
  private workspaceId: string;

  /**
   * Initialize the WorkflowManager with required dependencies
   *
   * @param llmManager - Manager for LLM provider interactions
   * @param vectorDB - Vector database for context storage and retrieval
   * @param gitManager - Git integration for version control
   */
  constructor(
    private llmManager: LLMManager,
    private vectorDB: VectorDB,
    private gitManager: GitManager
  ) {
    this.workflowRL = new AdaptiveWorkflowRL();
    this.workspaceId = `workspace_${Date.now()}`;
    this.metrics = {
      startTime: Date.now(),
      phaseStartTime: Date.now(),
      errors: 0,
      userFeedback: [],
      iterations: 0,
    };

    this.initializeCollaboration();
  }

  /**
   * Start a new development workflow from a project idea
   *
   * @param idea - The user's project description or idea
   * @param option - Optional workflow configuration or starting phase
   * @returns Promise that resolves when workflow initialization is complete
   *
   * @example
   * ```typescript
   * await workflowManager.startWorkflow(
   *   "Create a task management app with React and TypeScript"
   * );
   * ```
   */
  async startWorkflow(idea: string, option?: string): Promise<void> {
    this.projectIdea = idea;
    this.currentPhase = 0;

    try {
      let prompt = idea;
      if (option === 'letPanelDecide') {
        prompt = await this.llmManager.conference(`Refine this project idea: ${idea}`);
      }

      // Step 2: Conferencing
      const discussion = await this.llmManager.conference(
        `Discuss project: ${prompt}. Propose tech stack, estimates, plan.`
      );

      this.buildPlan = await this.llmManager.voteOnDecision(discussion, [
        'Approve Plan',
        'Need Questions',
      ]);

      if (this.buildPlan === 'Need Questions') {
        const questions = await this.llmManager.queryLLM(
          0,
          `Generate 5-10 questions for clarification on ${prompt}`
        );
        const answers = await vscode.window.showInputBox({
          prompt: `Please answer these questions: ${questions}`,
        });
        if (answers) {
          this.buildPlan = await this.llmManager.conference(
            `Incorporate answers: ${answers}. Finalize plan.`
          );
        }
      }

      // Store in vector DB
      const embedding = await this.vectorDB.getEmbedding(this.buildPlan);
      await this.vectorDB.addEmbedding('buildPlan', embedding, { plan: this.buildPlan });

      vscode.window.showInformationMessage('Build Plan ready! Proceeding to phases.');
      await this.executePhase();
    } catch (error: any) {
      vscode.window.showErrorMessage(`Workflow failed: ${error.message}`);
    }
  }

  async executePhase() {
    const phase = this.phases[this.currentPhase];
    this.metrics.phaseStartTime = Date.now();

    try {
      // Get current workflow state for RL
      const currentState = this.getCurrentWorkflowState();

      // Get RL recommendation for next action
      const recommendedAction = this.workflowRL.getBestAction(currentState);

      // Apply RL action if not 'continue'
      if (recommendedAction.type !== 'continue') {
        const actionResult = await this.applyRLAction(recommendedAction, phase);
        if (actionResult.shouldReturn) {
          return;
        }
      }

      // Notify collaboration server about phase start
      this.collaborationServer?.broadcastToWorkspace(this.workspaceId, 'phase_started', {
        phase,
        timestamp: Date.now(),
        projectIdea: this.projectIdea,
      });

      // Enhanced context retrieval using vector DB
      const contextQuery = `${phase} for ${this.projectIdea}`;
      const contextEmbedding = await this.vectorDB.getEmbedding(contextQuery);
      const relevantContext = await this.vectorDB.queryEmbedding(contextEmbedding, 3);

      const contextText = relevantContext
        .map(item => item.metadata)
        .filter(meta => meta && typeof meta === 'object')
        .map(meta => meta.plan || meta.content || '')
        .join('\n');

      // Generate phase content with enhanced prompting
      const phasePrompt = this.buildEnhancedPrompt(phase, contextText);
      const output = await this.llmManager.conference(phasePrompt);

      // Process and validate output
      const processedOutput = await this.processPhaseOutput(output, phase);

      // Write output to file with better organization
      await this.writePhaseOutput(processedOutput, phase);

      // Git commit with detailed message
      await this.gitManager.commit(`Phase ${phase} complete - ${this.getPhaseMetrics()}`);

      // Enhanced review with multiple perspectives
      const review = await this.conductPhaseReview(processedOutput, phase);

      // Intelligent suggestions using context
      const suggestions = await this.generateIntelligentSuggestions(
        phase,
        processedOutput,
        contextText
      );

      // User interaction with better UX
      const userDecision = await this.getUserDecision(suggestions, review);
      const userFeedback = await this.processUserDecision(
        userDecision,
        suggestions,
        processedOutput,
        phase
      );

      // Update RL with feedback
      const newState = this.getCurrentWorkflowState();
      const reward = this.workflowRL.calculateReward(
        currentState,
        recommendedAction,
        newState,
        true, // Phase succeeded
        userFeedback
      );

      this.workflowRL.updateQValue(currentState, recommendedAction, reward, newState);

      // Store phase results in vector DB for future context
      await this.storePhaseContext(phase, processedOutput, review);

      this.metrics.iterations++;
      this.currentPhase++;

      if (this.currentPhase < this.phases.length) {
        vscode.window.showInformationMessage(
          `Phase ${phase} complete! Next: ${this.phases[this.currentPhase]}. Click "Acknowledge & Proceed".`
        );
      } else {
        await this.completeProject();
      }
    } catch (error: any) {
      await this.handlePhaseError(error, phase);
    }
  }

  proceedToNextPhase() {
    if (this.currentPhase < this.phases.length) {
      this.executePhase();
    }
  }

  // Supporting methods for enhanced workflow

  private async initializeCollaboration(): Promise<void> {
    try {
      this.collaborationServer = new CollaborationServer(3001);
      await this.collaborationServer.start();
      console.log('Collaboration server initialized');
    } catch (error) {
      console.warn('Failed to start collaboration server:', error);
    }
  }

  private getCurrentWorkflowState(): any {
    const totalTime = Date.now() - this.metrics.startTime;

    return {
      currentPhase: this.phases[this.currentPhase],
      projectComplexity: this.estimateProjectComplexity(),
      userSatisfaction: this.calculateUserSatisfaction(),
      errorRate: this.metrics.errors / Math.max(1, this.metrics.iterations),
      timeSpent: Math.min(1, totalTime / (1000 * 60 * 60)), // Normalize to hours
    };
  }

  private estimateProjectComplexity(): number {
    // Simple heuristic based on project description and phases
    const ideaLength = this.projectIdea.length;
    const complexityKeywords = [
      'api',
      'database',
      'authentication',
      'real-time',
      'machine learning',
      'ai',
      'blockchain',
    ];
    const matches = complexityKeywords.filter(keyword =>
      this.projectIdea.toLowerCase().includes(keyword)
    ).length;

    return Math.min(1, (ideaLength / 500 + matches / complexityKeywords.length) / 2);
  }

  private calculateUserSatisfaction(): number {
    if (this.metrics.userFeedback.length === 0) return 0.7; // Default neutral
    return (
      this.metrics.userFeedback.reduce((sum, rating) => sum + rating, 0) /
      this.metrics.userFeedback.length
    );
  }

  private async applyRLAction(action: any, phase: string): Promise<{ shouldReturn: boolean }> {
    switch (action.type) {
      case 'skip':
        vscode.window.showInformationMessage(`RL suggests skipping ${phase} phase`);
        this.currentPhase++;
        return { shouldReturn: true };

      case 'repeat':
        vscode.window.showInformationMessage(`RL suggests repeating ${phase} phase`);
        // Phase will be re-executed
        return { shouldReturn: false };

      case 'optimize':
        vscode.window.showInformationMessage(`RL suggests optimizing ${phase} phase`);
        // Continue with optimization hints
        return { shouldReturn: false };

      default:
        return { shouldReturn: false };
    }
  }

  private buildEnhancedPrompt(phase: string, contextText: string): string {
    const basePrompt = `Execute ${phase} for project: ${this.projectIdea}. Plan: ${this.buildPlan}`;

    if (contextText) {
      return `${basePrompt}\n\nRelevant context from previous work:\n${contextText}\n\nPlease build upon this context and ensure consistency.`;
    }

    return basePrompt;
  }

  private async processPhaseOutput(output: string, phase: string): Promise<string> {
    // Add phase-specific processing
    let processed = output;

    // Add timestamp and phase info
    processed = `# ${phase} Phase Output\n\n*Generated: ${new Date().toISOString()}*\n\n${processed}`;

    // Validate output based on phase
    if (phase === 'Planning' && !processed.includes('architecture')) {
      processed +=
        '\n\n## Architecture Notes\n*Architecture details should be included in planning phase.*';
    }

    return processed;
  }

  private async writePhaseOutput(output: string, phase: string): Promise<void> {
    if (!vscode.workspace.workspaceFolders) return;

    const workspaceRoot = vscode.workspace.workspaceFolders[0].uri.fsPath;
    const timestamp = new Date().toISOString().split('T')[0];
    const fileName = `${phase.toLowerCase()}_${timestamp}.md`;
    const filePath = vscode.Uri.file(path.join(workspaceRoot, 'astraforge_output', fileName));

    // Ensure directory exists
    const dirPath = path.join(workspaceRoot, 'astraforge_output');
    try {
      await vscode.workspace.fs.createDirectory(vscode.Uri.file(dirPath));
    } catch {
      // Directory might already exist
    }

    await vscode.workspace.fs.writeFile(filePath, Buffer.from(output));

    // Also update the latest version
    const latestPath = vscode.Uri.file(
      path.join(workspaceRoot, 'astraforge_output', `${phase.toLowerCase()}_latest.md`)
    );
    await vscode.workspace.fs.writeFile(latestPath, Buffer.from(output));
  }

  private getPhaseMetrics(): string {
    const phaseTime = Date.now() - this.metrics.phaseStartTime;
    return `Time: ${Math.round(phaseTime / 1000)}s, Iteration: ${this.metrics.iterations + 1}`;
  }

  private async conductPhaseReview(output: string, phase: string): Promise<string> {
    const reviewPrompt = `Review this ${phase} phase output for quality, completeness, and potential issues:\n\n${output}`;
    return await this.llmManager.conference(reviewPrompt);
  }

  private async generateIntelligentSuggestions(
    phase: string,
    output: string,
    contextText: string
  ): Promise<string> {
    const suggestionPrompt = `Based on the ${phase} output and context, suggest 3-5 specific improvements or innovations:\n\nOutput:\n${output}\n\nContext:\n${contextText}`;
    return await this.llmManager.queryLLM(0, suggestionPrompt);
  }

  private async getUserDecision(suggestions: string, review: string): Promise<string> {
    const options = [
      'Proceed as planned',
      'Apply suggestions',
      'Request modifications',
      'Get more details',
    ];

    const choice = await vscode.window.showQuickPick(options, {
      placeHolder: `Review: ${review.substring(0, 100)}... | Suggestions: ${suggestions.substring(0, 100)}...`,
      canPickMany: false,
    });

    return choice || 'Proceed as planned';
  }

  private async processUserDecision(
    decision: string,
    suggestions: string,
    output: string,
    phase: string
  ): Promise<number> {
    let feedback = 0.7; // Default neutral feedback

    switch (decision) {
      case 'Proceed as planned':
        feedback = 0.8;
        break;

      case 'Apply suggestions': {
        feedback = 0.9;
        const improvedOutput = await this.llmManager.conference(
          `Apply these suggestions: ${suggestions} to improve: ${output}`
        );
        await this.writePhaseOutput(improvedOutput, `${phase}_improved`);
        break;
      }

      case 'Request modifications': {
        feedback = 0.5;
        const modification = await vscode.window.showInputBox({
          prompt: 'What modifications would you like?',
        });
        if (modification) {
          const modifiedOutput = await this.llmManager.conference(
            `Apply these modifications: ${modification} to: ${output}`
          );
          await this.writePhaseOutput(modifiedOutput, `${phase}_modified`);
        }
        break;
      }

      case 'Get more details': {
        feedback = 0.6;
        const details = await this.llmManager.queryLLM(
          0,
          `Provide more detailed explanation for: ${output}`
        );
        vscode.window.showInformationMessage(`Details: ${details.substring(0, 200)}...`);
        break;
      }
    }

    this.metrics.userFeedback.push(feedback);
    return feedback;
  }

  private async storePhaseContext(phase: string, output: string, review: string): Promise<void> {
    const contextData = {
      phase,
      content: output,
      review,
      timestamp: Date.now(),
      projectIdea: this.projectIdea,
    };

    const embedding = await this.vectorDB.getEmbedding(
      `${phase} ${this.projectIdea} ${output.substring(0, 500)}`
    );
    await this.vectorDB.addEmbedding(`phase_${phase}_${Date.now()}`, embedding, contextData);
  }

  private async handlePhaseError(error: any, phase: string): Promise<void> {
    this.metrics.errors++;

    console.error(`Phase ${phase} error:`, error);

    const errorMessage = `Phase ${phase} encountered an error: ${error.message}`;
    const options = ['Retry phase', 'Skip phase', 'Abort workflow'];

    const choice = await vscode.window.showErrorMessage(errorMessage, ...options);

    switch (choice) {
      case 'Retry phase':
        await this.executePhase();
        break;

      case 'Skip phase':
        this.currentPhase++;
        if (this.currentPhase < this.phases.length) {
          await this.executePhase();
        } else {
          await this.completeProject();
        }
        break;

      case 'Abort workflow':
        vscode.window.showErrorMessage('Workflow aborted by user');
        break;
    }
  }

  private async completeProject() {
    try {
      // Generate comprehensive final report with metrics
      const totalTime = Date.now() - this.metrics.startTime;
      const rlStats = this.workflowRL.getStats();

      const report = await this.llmManager.queryLLM(
        0,
        `Generate a comprehensive final report for ${this.projectIdea}. Include project summary, key achievements, and lessons learned.`
      );

      const bonuses = await this.llmManager.queryLLM(
        0,
        `Suggest 5 innovative A+ enhancements for ${this.projectIdea}, considering cutting-edge technologies like AI, blockchain, and real-time collaboration.`
      );

      const finalReport = `# AstraForge Project Completion Report

## Project: ${this.projectIdea}

### Execution Metrics
- **Total Time**: ${Math.round(totalTime / 1000 / 60)} minutes
- **Phases Completed**: ${this.currentPhase} / ${this.phases.length}
- **Iterations**: ${this.metrics.iterations}
- **Errors Encountered**: ${this.metrics.errors}
- **Average User Satisfaction**: ${this.calculateUserSatisfaction().toFixed(2)}

### AI Learning Metrics
- **RL States Explored**: ${rlStats.totalStates}
- **Actions Learned**: ${rlStats.totalActions}
- **Current Exploration Rate**: ${rlStats.explorationRate.toFixed(3)}

## Project Report
${report}

## Enhancement Suggestions
${bonuses}

---
*Generated by AstraForge IDE - ${new Date().toISOString()}*
`;

      vscode.window.showInformationMessage(
        'üéâ Project Complete! Check the final report for details and enhancements.'
      );

      // Save comprehensive final report
      if (vscode.workspace.workspaceFolders) {
        const workspaceRoot = vscode.workspace.workspaceFolders[0].uri.fsPath;
        const reportPath = vscode.Uri.file(
          path.join(workspaceRoot, 'astraforge_output', 'FINAL_REPORT.md')
        );
        await vscode.workspace.fs.writeFile(reportPath, Buffer.from(finalReport));

        // Open the report
        const doc = await vscode.workspace.openTextDocument(reportPath);
        await vscode.window.showTextDocument(doc);
      }

      // Notify collaboration server
      this.collaborationServer?.broadcastToWorkspace(this.workspaceId, 'project_completed', {
        projectIdea: this.projectIdea,
        metrics: this.metrics,
        timestamp: Date.now(),
      });
    } catch (error: any) {
      vscode.window.showErrorMessage(`Project completion failed: ${error.message}`);
    }
  }
}
